mapper.select:
SELECT A.ROUT_ID, A.NODE_ID, A.NODE_SN, A.NODE_TYPE, A.NODE_NM, A.NODE_ENM, A.STTN_ID, A.STTN_NO, A.CRS_ID, A.GPS_X, A.GPS_Y, A.TM_X, A.TM_Y, A.ACCRU_LEN, A.UPD_DTM, A.UPD_ID FROM BRT_MAIN_ROUT_NODE_INFO A INNER JOIN BMS_ROUT_MST R ON R.DEL_YN !='Y' AND A.ROUT_ID = R.ROUT_ID WHERE A.ROUT_ID = #{ROUT_ID}
AND (NODE_ID LIKE CONCAT('%',#{CONTENT},'%') || NODE_NM LIKE CONCAT('%',#{CONTENT},'%'))
ORDER BY A.NODE_SN ASC

mapper.delete:
DELETE FROM BRT_MAIN_ROUT_NODE_INFO
WHERE ROUT_ID = #{ROUT_ID}
AND NODE_ID = #{NODE_ID}
AND NODE_SN = #{NODE_SN}

mapper.select:
SELECT A.ROUT_ID, A.NODE_ID, A.NODE_NM, A.NODE_TYPE, A.NODE_SN, A.GPS_X, A.GPS_Y, A.STTN_ID, A.STTN_NO, A.CRS_ID, A.ACCRU_LEN FROM BMS_ROUT_NODE_CMPSTN A INNER JOIN BMS_ROUT_MST R ON A.ROUT_ID = R.ROUT_ID AND R.DEL_YN !='Y' WHERE A.ROUT_ID = #{ROUT_ID}
AND A.NODE_TYPE =#{NODE_TYPE}
AND (A.NODE_TYPE = 'NT001' || A.NODE_TYPE = 'NT002')
AND (A.NODE_ID LIKE CONCAT('%',#{CONTENT},'%') || A.NODE_NM LIKE CONCAT('%',#{CONTENT},'%'))
ORDER BY A.NODE_SN ASC
