mapper.update:
UPDATE BRT_OPER_PL_MST
SET ST_STTN_ID = #{ST_STTN_ID}, ED_STTN_ID = #{ED_STTN_ID}
	, OPER_CNT = #{OPER_CNT}, ALLOC_CNT = #{ALLOC_CNT}, FST_TM = #{FST_TM}, LST_TM = #{LST_TM}, AM_PEAK_ST_TM = #{AM_PEAK_ST_TM}, AM_PEAK_ED_TM = #{AM_PEAK_ED_TM}
	, PM_PEAK_ST_TM = #{PM_PEAK_ST_TM}, PM_PEAK_ED_TM = #{PM_PEAK_ED_TM}, AM_PEAK= #{AM_PEAK}, PM_PEAK = #{PM_PEAK}, NONE_PEAK = #{NONE_PEAK}, REMARK = #{REMARK}
	, UPD_DTM = #{UPD_DTM}, UPD_ID = #{SSN_USER_ID} 
WHERE REP_ROUT_ID = #{REP_ROUT_ID} AND DAY_DIV = #{DAY_DIV}  AND WAY_DIV = #{WAY_DIV}

UPDATE BRT_OPER_PL_ROUT_INFO
SET ROUT_ID = #{ROUT_ID}, OPER_SN = #{OPER_SN}, ROUT_ST_TM = #{ROUT_ST_TM}, ROUT_ED_TM = #{ROUT_ED_TM}, UPD_DTM = #{UPD_DTM}, UPD_ID = #{SSN_USER_ID} 
WHERE REP_ROUT_ID = #{REP_ROUT_ID} AND DAY_DIV = #{DAY_DIV} AND WAY_DIV = #{WAY_DIV} AND OPER_SN = #{OLD_OPER_SN}
