mapper.select:
SELECT A.REP_ROUT_ID, A.ROUT_ID, A.DAY_DIV, A.WAY_DIV, A.OPER_SN, A.ALLOC_NO, A.COR_ID, A.REST_TM, A.ROUT_ST_TM, A.ROUT_ED_TM, A.UPD_DTM, A.UPD_ID, B.REP_ROUT_NM, B.WAY_ASC_NM, B.WAY_DESC_NM, C.ROUT_NM, C.ROAT_TM, D.COR_NM, D.COR_COLOR ,F.VHC_ID ,F.VHC_NO ,I.VHC_ID AS SEC_VHC_ID ,I.VHC_NO AS SEC_VHC_NO ,H.DRV_ID ,H.DRV_NM ,(SELECT DRV_ID FROM BMS_DRV_MST S WHERE G.SEC_DRV_ID = S.DRV_ID) AS SEC_DRV_ID ,(SELECT DRV_NM FROM BMS_DRV_MST S WHERE G.SEC_DRV_ID = S.DRV_ID) AS SEC_DRV_NM FROM BRT_OPER_ALLOC_PL_ROUT_INFO A INNER JOIN BMS_REP_ROUT_MST B ON A.REP_ROUT_ID = B.REP_ROUT_ID LEFT OUTER JOIN BMS_ROUT_MST C ON A.ROUT_ID = C.ROUT_ID AND C.DEL_YN != 'Y' LEFT OUTER JOIN BRT_COR_MST D ON A.COR_ID = D.COR_ID LEFT JOIN BRT_ALLOC_PL_MST E ON A.REP_ROUT_ID = E.REP_ROUT_ID AND A.DAY_DIV = E.DAY_DIV AND A.ALLOC_NO = E.ALLOC_NO AND A.WAY_DIV = E.WAY_DIV LEFT JOIN BMS_VHC_MST F ON E.VHC_ID = F.VHC_ID LEFT JOIN BMS_VHC_MST I ON E.SEC_VHC_ID = I.VHC_ID LEFT JOIN BRT_DRV_ALLOC_INFO G ON A.REP_ROUT_ID = G.REP_ROUT_ID AND A.DAY_DIV = G.DAY_DIV AND A.ALLOC_NO = G.ALLOC_NO LEFT JOIN BMS_DRV_MST H ON G.DRV_ID = H.DRV_ID WHERE A.REP_ROUT_ID = #{REP_ROUT_ID} AND A.DAY_DIV = #{DAY_DIV}
AND (H.DRV_NM LIKE CONCAT('%',#{CONTENT1},'%') OR F.VHC_NO LIKE CONCAT('%',#{CONTENT1},'%'))
ORDER BY REP_ROUT_ID, DAY_DIV, ALLOC_NO, OPER_SN
