mapper.select:
SELECT A.REP_ROUT_ID ,A.DAY_DIV ,A.WAY_DIV ,B.REP_ROUT_NM ,MAX(CASE A.WAY_DIV WHEN 'WD001' THEN C.STTN_NM END) AS ASC_ST_STTN_NM ,MAX(CASE A.WAY_DIV WHEN 'WD001' THEN D.STTN_NM END) AS ASC_ED_STTN_NM ,MAX(CASE A.WAY_DIV WHEN 'WD002' THEN C.STTN_NM END) AS DESC_ST_STTN_NM ,MAX(CASE A.WAY_DIV WHEN 'WD002' THEN D.STTN_NM END) AS DESC_ED_STTN_NM FROM BRT_OPER_PL_MST A LEFT JOIN BMS_REP_ROUT_MST B ON A.REP_ROUT_ID = B.REP_ROUT_ID LEFT JOIN BMS_STTN_MST C ON A.ST_STTN_ID = C.STTN_ID LEFT JOIN BMS_STTN_MST D ON A.ED_STTN_ID = D.STTN_ID
B.USE_YN = 'Y' AND B.DEL_YN = 'N'
AND A.REP_ROUT_ID LIKE CONCAT('%',#{CONTENT3},'%')
AND A.REP_ROUT_ID = #{CONTENT1}
AND A.DAY_DIV = #{CONTENT2}
GROUP BY B.REP_ROUT_ID, A.DAY_DIV ORDER BY B.REP_ROUT_NM, A.DAY_DIV, A.WAY_DIV DESC

mapper.insert:
INSERT INTO BRT_ALLOC_PL_MST(
	REP_ROUT_ID
	,DAY_DIV
	,ALLOC_NO
	,WAY_DIV
	,VHC_ID
	,SEC_VHC_ID
	,SEC_TRAN_TM
	,UPD_DTM
	,UPD_ID
)VALUES(
	#{REP_ROUT_ID}
	,#{DAY_DIV}
	,#{ALLOC_NO}
	,#{WAY_DIV}
	,#{VHC_ID}
	,#{SEC_VHC_ID}
	,#{SEC_TRAN_TM}
	,#{UPD_DTM}
	,#{SSN_USER_ID}
)ON DUPLICATE KEY UPDATE
	VHC_ID = VALUES(VHC_ID)
	,SEC_VHC_ID = VALUES(SEC_VHC_ID)
	,SEC_TRAN_TM = VALUES(SEC_TRAN_TM)
	,UPD_DTM = VALUES(UPD_DTM)
	,UPD_ID	 = VALUES(UPD_ID)

INSERT INTO BRT_DRV_ALLOC_INFO(
	REP_ROUT_ID
	,ALLOC_NO
	,DAY_DIV
	,DRV_ID
	,SEC_DRV_ID
	,THR_DRV_ID
	,SEC_TRAN_TM
	,THR_TRAN_TM
)VALUES(
	#{REP_ROUT_ID}
	,#{ALLOC_NO}
	,#{DAY_DIV}
	,#{DRV_ID}
	,#{SEC_DRV_ID}
	,#{THR_DRV_ID}
	,#{SEC_TRAN_TM}
	,#{THR_TRAN_TM}
)ON DUPLICATE KEY UPDATE
	DRV_ID = VALUES(DRV_ID)
	,SEC_DRV_ID = VALUES(SEC_DRV_ID)
	,THR_DRV_ID = VALUES(THR_DRV_ID)
	,SEC_TRAN_TM = VALUES(SEC_TRAN_TM)
	,THR_TRAN_TM = VALUES(THR_TRAN_TM)

INSERT INTO BRT_DAY_ALLOC_PL_INFO(
	OPER_DT
	,REP_ROUT_ID
	,WAY_DIV
	,ALLOC_NO
	,VHC_ID
	,SEC_VHC_ID
	,SEC_TRAN_TM
	,UPD_DTM
)VALUES(
	#{OPER_DT}
	,#{REP_ROUT_ID}
	,#{WAY_DIV}
	,#{ALLOC_NO}
	,#{VHC_ID}
	,#{SEC_VHC_ID}
	,#{SEC_TRAN_TM}
	,#{UPD_DTM}
)ON DUPLICATE KEY UPDATE
	VHC_ID = VALUES(VHC_ID)
	,SEC_VHC_ID = VALUES(SEC_VHC_ID)
	,SEC_TRAN_TM = VALUES(SEC_TRAN_TM)
	,UPD_DTM = VALUES(UPD_DTM)

INSERT INTO BRT_DAY_DRV_ALLOC_INFO(
	OPER_DT
	,REP_ROUT_ID
	,ALLOC_NO
	,DAY_DIV
	,DRV_ID
	,SEC_DRV_ID
	,THR_DRV_ID
	,SEC_TRAN_TM
	,THR_TRAN_TM
)VALUES(
	#{OPER_DT}
	,#{REP_ROUT_ID}
	,#{ALLOC_NO}
	,#{DAY_DIV}
	,#{DRV_ID}
	,#{SEC_DRV_ID}
	,#{THR_DRV_ID}
	,#{SEC_TRAN_TM}
	,#{THR_TRAN_TM}
)ON DUPLICATE KEY UPDATE
	DRV_ID = VALUES(DRV_ID)
	,SEC_DRV_ID = VALUES(SEC_DRV_ID)
	,THR_DRV_ID = VALUES(THR_DRV_ID)
	,SEC_TRAN_TM = VALUES(SEC_TRAN_TM)
	,THR_TRAN_TM = VALUES(THR_TRAN_TM)

mapper.update:
UPDATE
	BRT_DAY_ALLOC_PL_INFO
SET
	VHC_ID = #{VHC_ID}
	,SEC_VHC_ID = #{SEC_VHC_ID}
	,SEC_TRAN_TM = #{SEC_TRAN_TM}
	,UPD_DTM = #{UPD_DTM}
WHERE
	REP_ROUT_ID = #{REP_ROUT_ID}
AND
	ALLOC_NO = #{ALLOC_NO}
AND
	OPER_DT = #{OPER_DT}

UPDATE
	BRT_DAY_DRV_ALLOC_INFO
SET
	DRV_ID = #{DRV_ID}
	,SEC_DRV_ID = #{SEC_DRV_ID}
	,THR_DRV_ID = #{THR_DRV_ID}
	,SEC_TRAN_TM = #{SEC_TRAN_TM}
	,THR_TRAN_TM = #{THR_TRAN_TM}
WHERE
	REP_ROUT_ID = #{REP_ROUT_ID}
AND
	ALLOC_NO = #{ALLOC_NO}
AND
	DAY_DIV = #{DAY_DIV}
AND
	OPER_DT = #{OPER_DT}
