mapper.select:
SELECT A.REP_ROUT_ID ,A.DAY_DIV ,(SELECT STTN_NM FROM BMS_STTN_MST WHERE STTN_ID = A.ST_STTN_ID AND DEL_YN!='Y') ST_STTN_NM ,(SELECT STTN_NM FROM BMS_STTN_MST WHERE STTN_ID = A.ED_STTN_ID AND DEL_YN!='Y') ED_STTN_NM ,B.REP_ROUT_NM FROM BRT_OPER_PL_MST A LEFT JOIN BMS_REP_ROUT_MST B ON A.REP_ROUT_ID = B.REP_ROUT_ID
AND A.REP_ROUT_ID = #{CONTENT1}
AND A.DAY_DIV = #{CONTENT2}
AND A.VHC_NO LIKE CONCAT('%', #{CONTENT3}, '%')

mapper.update:
UPDATE
	BRT_ALLOC_PL_MST
SET
	VHC_ID = #{VHC_ID}
	,UPD_DTM = #{UPD_DTM}
	,UPD_ID	 = #{SSN_USER_ID}
WHERE
	REP_ROUT_ID = #{REP_ROUT_ID}
AND
	DAY_DIV = #{DAY_DIV}
AND
	ALLOC_NO = #{ALLOC_NO};

UPDATE
	BRT_DRV_ALLOC_INFO
SET
	DRV_ID = #{DRV_ID}
	,SEC_DRV_ID = #{SEC_DRV_ID}
	,THR_DRV_ID = #{THR_DRV_ID}
	,SEC_TRAN_TM = #{SEC_TRAN_TM}
	,THR_TRAN_TM = #{THR_TRAN_TM}
WHERE
	REP_ROUT_ID = #{REP_ROUT_ID}
AND
	ALLOC_NO = #{ALLOC_NO};
