mapper.select:
SELECT DATE_FORMAT(A.OPER_DT, '%Y-%m-%d') OPER_DT ,A.REP_ROUT_ID ,A.DAY_DIV ,A.WAY_DIV ,A.OPER_SN ,A.NODE_ID ,A.NODE_SN ,A.ALLOC_NO ,A.COR_ID ,A.NODE_TYPE ,A.ARRV_TM ,A.DPRT_TM
,DATE_FORMAT(A.UPD_DTM, '%Y-%m-%d') UPD_DTM ,B.REP_ROUT_NM ,B.WAY_ASC_NM ,B.WAY_DESC_NM ,C.ROUT_ID ,C.NODE_NM ,C.ACCRU_LEN FROM BRT_DAY_OPER_ALLOC_PL_NODE_INFO A INNER JOIN BMS_REP_ROUT_MST B ON A.REP_ROUT_ID = B.REP_ROUT_ID INNER JOIN BMS_ROUT_NODE_CMPSTN C ON A.ROUT_ID = C.ROUT_ID AND A.NODE_ID = C.NODE_ID AND A.NODE_SN = C.NODE_SN
AND C.NODE_TYPE = #{NODE_TYPE}
WHERE A.OPER_DT = #{OPER_DT} AND A.DAY_DIV = #{DAY_DIV} AND C.ROUT_ID = #{ROUT_ID} AND A.NODE_TYPE = 'NT002'
AND A.OPER_SN = #{OPER_SN}
ORDER BY OPER_SN, NODE_SN

SELECT COUNT(*) CNT FROM BRT_OPER_ALLOC_PL_NODE_INFO A INNER JOIN BMS_ROUT_NODE_CMPSTN B ON A.ROUT_ID = B.ROUT_ID AND A.NODE_ID = B.NODE_ID AND A.NODE_SN = B.NODE_SN
AND B.NODE_TYPE = #{NODE_TYPE}
WHERE A.DAY_DIV = #{DAY_DIV} AND B.ROUT_ID = #{ROUT_ID} AND A.NODE_TYPE = 'NT002' GROUP BY OPER_SN ORDER BY OPER_SN
