mapper.update:
UPDATE			BMS_FCLT_INFO
SET				FCLT_ID = #{FCLT_ID}
				,MAKER = #{MAKER}
				,FCLT_KIND = #{FCLT_KIND}
				,INST_LOC = #{INST_LOC}
				,MODEL_NM = #{MODEL_NM}
				,CLSFCTN_ID = #{CLSFCTN_ID}	
				,MNG_ID = #{MNG_ID}	
				,FCLT_COORDS = #{FCLT_COORDS}	
				,USE_YN = #{USE_YN}	
				,CTR_YN = #{CTR_YN}	
				,REMARK = #{REMARK}
				,INST_DT = STR_TO_DATE(#{INST_DT}, '%Y%m%d')
				,BUY_DT = STR_TO_DATE(#{BUY_DT}, '%Y%m%d')

WHERE			FCLT_ID = #{FCLT_ID}
