SELECT A.NODE_ID , A.MOCK_NODE_ID , A.NODE_NM , A.NODE_ENM , A.NODE_TYPE , A.GPS_X , A.GPS_Y , A.TM_X , A.TM_Y , A.ENTRY_RECOG_RADS , A.ADVNC_RECOG_RADS , A.USE_YN , A.UPD_DTM , A.UPD_ID , IF(D.CNT > 0 , 'CE001','CE002') COND_ERROR , E.ROUT_ID , E.NODE_SN , H.STTN_FCLT_TYPE FROM BMS_ROUT_NODE_CMPSTN E INNER JOIN BMS_ROUT_MST G ON E.ROUT_ID = G.ROUT_ID AND G.DEL_YN !='Y' LEFT JOIN BMS_ROUT_LINK_CMPSTN F ON E.ROUT_ID = F.ROUT_ID AND E.LINK_ID = F.LINK_ID LEFT JOIN BMS_NODE_MST A ON E.NODE_ID = A.NODE_ID LEFT JOIN (SELECT B.NODE_ID, COUNT(B.NODE_ID) CNT FROM BMS_FCLT_INFO B LEFT JOIN BMS_FCLT_COND_INFO C ON B.MNG_ID = C.MNG_ID WHERE FCLT_COND = 'FC021' GROUP BY NODE_ID) D ON A.NODE_ID = D.NODE_ID LEFT JOIN BMS_STTN_MST H ON A.NODE_ID = H.STTN_ID WHERE (A.NODE_TYPE = 'NT002') AND G.DEL_YN != 'Y'
AND G.REP_ROUT_ID = #{CONTENT1}
AND E.ROUT_ID = #{CONTENT2}
AND (A.NODE_ID LIKE CONCAT('%',#{CONTENT3},'%') || A.NODE_NM LIKE CONCAT('%',#{CONTENT3},'%'))
AND A.NODE_TYPE = #{CONTENT4}
GROUP BY A.NODE_ID ORDER BY E.NODE_SN

SELECT			ROUT_ID, ROUT_NM
FROM			BMS_ROUT_MST	


		REP_ROUT_ID = #{CONTENT1}
