mapper.select:
SELECT A.OPER_DT , A.REP_ROUT_ID , A.VHC_ID , A.ROUT_ID , A.ALLOC_NO , A.OPER_SN , A.NODE_ID , A.COR_ID , A.VHC_NO , A.DRV_ID , A.GPS_X , A.GPS_Y , A.TM_X , A.TM_Y , A.OPER_STS , A.BUS_STS , A.OBE_STS , A.SNSTVTY , A.DRV_ANGLE , A.CUR_SPD , A.ACLRTN_YN , A.DCLRTN_YN , A.CUR_STOP_TM , A.OPER_LEN , A.REP_ROUT_NM , A.NODE_SN , A.NODE_TYPE , A.ARRV_TM , A.DPRT_TM , A.LINK_ID , A.LINK_SN , A.LINK_SPD , A.GET_OFF_CNT , A.PSG_CNT , A.NEXT_NODE_ID , A.NEXT_NODE_ARRV_TM , A.DOOR_CD , A.DOOR_TM , B.REP_ROUT_NM , B.ROUT_NM , C.CHAS_NO , C.MAKER , D.DRV_NM , E.COMP_NM , A.PRV_PLCE_NM , F.NODE_TYPE
FROM BRT_CUR_OPER_INFO A LEFT JOIN BMS_ROUT_MST B ON A.ROUT_ID = B.ROUT_ID LEFT JOIN BMS_VHC_MST C ON A.VHC_ID = C.VHC_ID LEFT JOIN BMS_DRV_MST D ON A.DRV_ID = D.DRV_ID LEFT JOIN BMS_TRANSCOMP_MST E ON C.COMP_ID = E.COMP_ID LEFT JOIN BMS_NODE_MST F ON A.NEXT_NODE_ID = F.NODE_TYPE
WHERE A.OPER_DT = DATE_FORMAT(NOW(), '%Y/%m/%d') AND B.DEL_YN != 'Y'
AND A.REP_ROUT_ID = #{CONTENT1}
AND A.ROUT_ID = #{CONTENT2}
ORDER BY A.REP_ROUT_ID, A.ROUT_ID, VHC_ID

SELECT A.ROUT_ID , A.NODE_ID , A.NODE_SN , A.NODE_NM , A.NODE_TYPE , A.LINK_ID , A.STTN_ID , A.STTN_NO , A.CRS_ID , A.ORGA_ID , A.GPS_X , A.GPS_Y , A.LINK_NODE_YN , B.MORN_STD , IF(H.CNT > 0 , 'CE001','CE002') COND_ERROR , C.WAY_DIV
FROM BMS_ROUT_NODE_CMPSTN A LEFT JOIN BMS_ROUT_LINK_CMPSTN B ON A.ROUT_ID = B.ROUT_ID AND A.LINK_ID = B.LINK_ID LEFT JOIN BMS_STTN_MST C ON A.NODE_ID = C.STTN_ID LEFT JOIN BMS_ROUT_MST D ON A.ROUT_ID = D.ROUT_ID LEFT JOIN BMS_NODE_MST E ON A.NODE_ID = E.NODE_ID LEFT JOIN (SELECT F.NODE_ID, COUNT(F.NODE_ID) CNT FROM BMS_FCLT_INFO F LEFT JOIN BMS_FCLT_COND_INFO G ON F.MNG_ID = G.MNG_ID WHERE FCLT_COND = 'FC021' GROUP BY NODE_ID) H ON E.NODE_ID = H.NODE_ID WHERE A.NODE_TYPE = 'NT002' AND C.DEL_YN != 'Y'
AND D.REP_ROUT_ID = #{CONTENT1}
AND A.ROUT_ID = #{CONTENT2}
GROUP BY NODE_ID ORDER BY NODE_SN

SELECT DATE_FORMAT(A.OPER_DT, '%Y-%m-%d') OPER_DT , A.REP_ROUT_ID , A.VHC_ID , A.UPD_DTM , A.VHC_NO , A.DRV_ID , A.COR_ID , A.ROUT_ID , A.ALLOC_NO , A.OPER_SN , A.GPS_X_RAW , A.GPS_Y_RAW , A.DRV_ANGLE , A.GPS_X , A.GPS_Y , A.CUR_SPD , A.LINK_ID , A.NODE_ID , A.NODE_SN , A.OPER_STS , A.EVT_TYPE , A.EVT_DATA , A.STOP_TM , DATE_FORMAT(A.OCR_DTM, '%H:%i:%s') OCR_DTM , B.NODE_NM , C.ORGA_NM , D.ROUT_NM
FROM BRT_OPER_EVENT_HIS A LEFT JOIN BMS_NODE_MST B ON A.EVT_DATA = B.NODE_ID LEFT JOIN BMS_VOC_ORGA_INFO C ON A.EVT_DATA = C.ORGA_ID LEFT JOIN BMS_ROUT_MST D ON A.ROUT_ID = D.ROUT_ID
WHERE DATE_FORMAT(OPER_DT, '%Y-%m-%d') = CURDATE()
AND A.REP_ROUT_ID = #{CONTENT1}
AND A.ROUT_ID = #{CONTENT2}
AND A.VHC_NO LIKE CONCAT('%', #{CONTENT3}, '%')
ORDER BY UPD_DTM DESC LIMIT 100;

SELECT DATE_FORMAT(A.SEND_DATE, '%H:%i:%s') SEND_DATE , A.OPER_DT , A.REP_ROUT_ID , A.VHC_ID , A.DSPTCH_DIV , A.ROUT_ID , A.ALLOC_NO , A.OPER_SN , A.COR_ID , A.REP_ROUT_NM , A.ROUT_NM , A.VHC_NO , A.LINK_ID , A.NODE_ID , A.DSPTCH_KIND , A.SEND_GPS_X , A.SEND_GPS_Y , A.SEND_TM_X , A.SEND_TM_Y , A.FRNT_GPS_Y , A.FRNT_GPS_X , A.FRNT_TM_X , A.FRNT_TM_Y , A.REAR_GPS_X , A.REAR_GPS_Y , A.REAR_TM_X , A.REAR_TM_Y , A.DSPTCH_CONTS , B.ROUT_NM
FROM BRT_DSPTCH_LOG A LEFT JOIN BMS_ROUT_MST B ON A.ROUT_ID = B.ROUT_ID
WHERE DATE_FORMAT(SEND_DATE, '%Y-%m-%d') = CURDATE()
AND A.REP_ROUT_ID = #{CONTENT1}
AND A.ROUT_ID = #{CONTENT2}
AND A.VHC_NO LIKE CONCAT('%', #{CONTENT3}, '%')
ORDER BY SEND_DATE DESC LIMIT 100;

SELECT DATE_FORMAT(A.UPD_DTM, '%H:%i:%s') UPD_DTM , A.NODE_ID , A.REP_ROUT_ID , A.ROUT_ID , A.VHC_NO , A.CTRL_LV , A.STOP_SEC , A.CTRL_TYPE , A.CTRL_PHASE_NO , DATE_FORMAT(A.OCR_DTM, '%H:%i:%s') OCR_DTM , B.NODE_NM , C.ROUT_NM
FROM BMS_CRS_SIGOPER_EVENT_HIS A LEFT JOIN BMS_NODE_MST B ON A.NODE_ID = B.NODE_ID LEFT JOIN BMS_ROUT_MST C ON A.ROUT_ID = C.ROUT_ID
WHERE DATE_FORMAT(OCR_DTM, '%Y-%m-%d') = CURDATE()
AND A.REP_ROUT_ID = #{CONTENT1}
AND A.ROUT_ID = #{CONTENT2}
AND A.NODE_ID LIKE CONCAT('%', #{CONTENT3}, '%')
ORDER BY OCR_DTM DESC LIMIT 100;
