mapper.select:
SELECT A.ROUT_ID , A.NODE_ID , A.NODE_SN , A.NODE_NM , A.NODE_TYPE , A.LINK_ID , A.STTN_ID , A.STTN_NO , A.CRS_ID , A.ORGA_ID , A.GPS_X , A.GPS_Y , A.LINK_NODE_YN , B.MORN_STD , IF(H.CNT > 0 , 'CE001','CE002') COND_ERROR
FROM BMS_ROUT_NODE_CMPSTN A LEFT JOIN BMS_ROUT_LINK_CMPSTN B ON A.ROUT_ID = B.ROUT_ID AND A.LINK_ID = B.LINK_ID LEFT JOIN BMS_STTN_MST C ON A.NODE_ID = C.STTN_ID LEFT JOIN BMS_ROUT_MST D ON A.ROUT_ID = D.ROUT_ID LEFT JOIN BMS_NODE_MST E ON A.NODE_ID = E.NODE_ID LEFT JOIN (SELECT F.NODE_ID, COUNT(F.NODE_ID) CNT FROM BMS_FCLT_INFO F LEFT JOIN BMS_FCLT_COND_INFO G ON F.MNG_ID = G.MNG_ID WHERE FCLT_COND = 'FC021' GROUP BY NODE_ID) H ON E.NODE_ID = H.NODE_ID WHERE A.NODE_TYPE = 'NT002' AND C.DEL_YN != 'Y'
AND D.REP_ROUT_ID = #{CONTENT1}
AND A.ROUT_ID = #{CONTENT2}
AND (A.NODE_ID LIKE CONCAT('%',#{CONTENT3},'%') || A.NODE_NM LIKE CONCAT('%',#{CONTENT3},'%'))
ORDER BY NODE_SN

SELECT			ROUT_ID, ROUT_NM
FROM			BMS_ROUT_MST	


		REP_ROUT_ID = #{CONTENT1}
