	BRT_INCDNT_SCNRO_MST
SET 
	SCNRO_ID = #{SCNRO_ID}
	,SCNRO_NM = #{SCNRO_NM}
	,INCDNT_TYPE = #{INCDNT_TYPE}
	,INCDNT_RES_TYPE = #{INCDNT_RES_TYPE}
	,RES_SCNRO = #{RES_SCNRO}
	,REMARK = #{REMARK}
	,UPD_DTM = #{UPD_DTM}
	,UPD_ID = #{UPD_ID}
WHERE 
	SCNRO_ID = #{SCNRO_ID}

