		,	REP_ROUT_ID
		,	VHC_ID
		,	INCDNT_TYPE
		,	ROUT_ID

			,	ALLOC_NO

		,	INCDNT_DETAIL

		,	OPER_SN

		,	COR_ID
		,	OCR_DTM
		,	VHC_NO
		,	DRV_ID
		,	REP_ROUT_NM
		,	ROUT_NM
		,	LINK_ID
		,	NODE_ID
		,	GPS_X
		,	GPS_Y
		,	REMARK
		,	UPD_DTM
		)

VALUES	(
			#{INCDNT_ID}
		,	DATE_FORMAT(#{OPER_DT}, '%Y%m%d')
		,	#{REP_ROUT_ID}
		,	#{VHC_ID}
		,	#{INCDNT_TYPE}
		,	#{ROUT_ID}

		,	#{ALLOC_NO}

		,	#{INCDNT_DETAIL}

		,	#{OPER_SN}

		,	#{COR_ID}
		,	DATE_FORMAT(#{OCR_DTM}, '%Y-%m-%d %H:%i:%s')
		,	#{VHC_NO}
		,	#{DRV_ID}
		,	#{REP_ROUT_NM}
		,	#{ROUT_NM}
		,	#{LINK_ID}
		,	#{NODE_ID}
		,	#{GPS_X}
		,	#{GPS_Y}
		,	#{REMARK}
		,	#{UPD_DTM}	
		)

mapper.update:
UPDATE		BRT_INCDNT_INFO

SET			OPER_DT = DATE_FORMAT(#{OPER_DT}, '%Y-%m-%d')	
		,	REP_ROUT_ID = #{REP_ROUT_ID}
		,	VHC_ID = #{VHC_ID}
		,	INCDNT_TYPE = #{INCDNT_TYPE}

		,	ALLOC_NO = #{ALLOC_NO}

		,	ROUT_ID = #{ROUT_ID}
		,	INCDNT_DETAIL = #{INCDNT_DETAIL}

		,	OPER_SN = #{OPER_SN}

		,	COR_ID = #{COR_ID}
		,	OCR_DTM = DATE_FORMAT(#{OCR_DTM}, '%Y-%m-%d %H:%i:%s')
		,	VHC_NO = #{VHC_NO}
		,	DRV_ID = #{DRV_ID}
		,	REP_ROUT_NM = #{REP_ROUT_NM}
		,	ROUT_NM = #{ROUT_NM}
		,	LINK_ID = #{LINK_ID}
		,	NODE_ID = #{NODE_ID}
		,	GPS_X = #{GPS_X}
		,	GPS_Y = #{GPS_Y}
		,	REMARK = #{REMARK}
		,	UPD_DTM = #{UPD_DTM}

WHERE		INCDNT_ID = #{INCDNT_ID}

mapper.select:
SELECT INCDNT_RES_ID , DATE_FORMAT(A.OPER_DT, '%Y-%m-%d') AS OPER_DT , A.REP_ROUT_ID , A.VHC_ID , A.INCDNT_TYPE , A.ROUT_ID , A.COR_ID , DATE_FORMAT(A.CLOSE_DTM, '%Y-%m-%d %H:%i:%s') AS CLOSE_DTM , A.GRP_ROUT_NM , A.ROUT_NM , A.VHC_NO , A.LINK_ID , A.NODE_ID , A.INCDNT_RES_TYPE , A.REMARK , A.PROG_DIV , A.CLSFCTN_ID , A.UPD_DTM , A.UPD_ID , B.ROUT_NM , C.REP_ROUT_NM , D.COR_NM , F.LINK_NM , G.NODE_NM , H.CLSFCTN_NM , H.PHONE
FROM BRT_INCDNT_RES_INFO A LEFT JOIN BMS_ROUT_MST B ON A.ROUT_ID = B.ROUT_ID LEFT JOIN BMS_REP_ROUT_MST C ON A.REP_ROUT_ID = C.REP_ROUT_ID LEFT JOIN BRT_COR_MST D ON A.COR_ID = D.COR_ID LEFT JOIN BMS_LINK_MST F ON A.LINK_ID = F.LINK_ID LEFT JOIN BMS_NODE_MST G ON A.NODE_ID = G.NODE_ID LEFT JOIN BMS_EMER_CLSFCTN_MST H ON A.CLSFCTN_ID = H.CLSFCTN_ID
WHERE A.OPER_DT = #{OPER_DT} AND A.REP_ROUT_ID = #{REP_ROUT_ID} AND A.VHC_ID = #{VHC_ID} AND A.INCDNT_TYPE = #{INCDNT_TYPE} AND A.ROUT_ID = #{ROUT_ID}

