	BMS_USER_NEWS_CFG_INFO 
SET 
	USER_NEWS_ID = #{USER_NEWS_ID}
	,NEWS_TITLE = #{NEWS_TITLE}
	,REMARK = #{REMARK}
	,NEWS_CONTENTS = #{NEWS_CONTENTS}
	,USE_YN = #{USE_YN}
	,CATEGORY = #{CATEGORY}
	,PROV_NM = #{PROV_NM}
	,PUB_DT = #{PUB_DT}
	,UPD_DTM = #{UPD_DTM}
	,UPD_ID = #{SSN_USER_ID}
WHERE 
	USER_NEWS_ID = #{USER_NEWS_ID}
