mapper.insert:


    		SELECT CONCAT('AR', LPAD(NEXTVAL(SEQ_BMS_VOC_RSV_INFO_0), 8, '0')) AS RSV_ID



		INSERT INTO BMS_VOC_RSV_INFO
				(
					RSV_ID
					,ROUT_ID
					,RSV_DT
				)

		VALUES	(
					#{RSV_ID}	
					,#{ROUT_ID}
					,#{RSV_DT}
				);

		INSERT INTO BMS_VOC_RSV_RST_INFO
				(
					RSV_ID
					,MNG_ID
					,COMPLETE_YN
				)
				VALUES(
					#{RSV_ID}
					,#{MNG_ID}
					,'N'
				);

mapper.select:
SELECT VHC.VHC_ID ,VHC.VHC_NO ,DVC.MNG_ID ,RST.PROCE_RST ,DATE_FORMAT(RST.SEND_DTM, '%Y-%m-%d') SEND_DTM ,VHC.CHAS_NO ,VHC.MAKER ,VHC.AREA ,TSM.COMP_NM ,DATE_FORMAT(VHC.RELS_DT, '%Y-%m-%d') RELS_DT ,VHC.MODEL_NM ,VHC.VHC_KIND ,VHC.ROUT_TYPE ,VHC.VHC_TYPE ,VHC.VHC_FUEL ,IF(VHC.USE_YN = 'Y', '사용', NULL) USE_YN ,VHC.REMARK ,DATE_FORMAT(RSV.RSV_DT, '%Y-%m-%d') RSV_DT ,IF(RST.COMPLETE_YN = 'N', '예약중', '예약종료') RSV_STATE FROM BMS_VHC_MST VHC LEFT JOIN BMS_DVC_INFO DVC ON VHC.VHC_ID = DVC.VHC_ID LEFT JOIN BMS_VOC_RSV_RST_INFO RST ON DVC.MNG_ID = RST.MNG_ID LEFT JOIN BMS_VOC_RSV_INFO RSV ON RST.RSV_ID = RSV.RSV_ID LEFT JOIN BMS_TRANSCOMP_MST TSM ON VHC.COMP_ID = TSM.COMP_ID WHERE VHC.VHC_ID = #{VHC_ID}
AND RST.COMPLETE_YN = 'N'
AND RST.COMPLETE_YN = 'Y'
AND DATE_FORMAT(RSV.RSV_DT, '%Y%m') = ${RSV_DT}
ORDER BY RSV.RSV_DT

