mapper.update:
update BMS_VDO_ORGA_INFO
set 	ORGA_NM = #{ORGA_NM},
		REMARK = #{REMARK},
		UPD_DTM = #{UPD_DTM},
		UPD_ID = #{SSN_USER_ID}
where	ORGA_ID = #{ORGA_ID}

mapper.delete:
delete
from BMS_VDO_ORGA_INFO
where ORGA_ID = #{ORGA_ID};

DELETE FROM BMS_VDO_ORGA_LIST 
WHERE ORGA_ID = #{ORGA_ID}

mapper.update:

update BMS_VDO_ORGA_LIST
set SN = #{SN}
where ORGA_ID = #{ORGA_ID}
and	VDO_ID = #{VDO_ID}

mapper.delete:
DELETE FROM BMS_VDO_ORGA_LIST 
WHERE ORGA_ID = #{ORGA_ID}
AND VDO_ID = #{VDO_ID}
