mapper.insert:
SELECT CONCAT('VR', LPAD(NEXTVAL(SEQ_BMS_VDO_RSV_INFO_0), 8, '0')) AS RSV_ID
INSERT INTO BMS_VDO_RSV_INFO ( RSV_ID, MNG_ID, RSV_DT, ORGA_ID, UPD_DTM, UPD_ID )
VALUES ( #{RSV_ID}, #{MNG_ID}, STR_TO_DATE(#{RSV_DT}, '%Y%m%d'), #{ORGA_ID}, #{UPD_DTM}, #{SSN_USER_ID} );




INSERT INTO
	BMS_VDO_RSV_RST_INFO(
		  RSV_ID
		, MNG_ID
		, COMPLETE_YN
		)
	VALUES(
		#{RSV_ID}
		, #{MNG_ID}
		, 'N'
	)

mapper.select:
SELECT VHC.VHC_ID ,VHC.VHC_NO ,DVC.MNG_ID ,RST.PROCE_RST ,DATE_FORMAT(RST.SEND_DTM, '%Y-%m-%d') SEND_DTM ,ORG.ORG_NM ,DVC.DVC_KIND ,DVC.INST_LOC ,DATE_FORMAT(RSV.RSV_DT, '%Y-%m-%d') RSV_DT ,IF(RST.COMPLETE_YN = 'N', '예약중', '예약종료') RSV_STATE FROM BMS_VDO_RSV_RST_INFO RST INNER JOIN BMS_VDO_RSV_INFO RSV ON RST.RSV_ID = RSV.RSV_ID LEFT JOIN BMS_DVC_INFO DVC ON RST.MNG_ID = DVC.MNG_ID LEFT JOIN BMS_VHC_MST VHC ON VHC.VHC_ID = DVC.VHC_ID LEFT JOIN BMS_ORG_MST ORG ON DVC.ORG_ID = ORG.ORG_ID AND ORG.ORG_DIV = 'OD008' LEFT JOIN BMS_DL_CD_INFO DL ON DVC.DVC_KIND = DL.DL_CD AND DL.CO_CD = 'DVC_KIND'
WHERE VHC.VHC_ID = #{VHC_ID}
AND RST.COMPLETE_YN = 'N'
AND RST.COMPLETE_YN = 'Y'
AND DATE_FORMAT(RSV.RSV_DT, '%Y%m') = ${RSV_DT}
ORDER BY RSV.RSV_DT
