INSERT INTO BMS_DESTI_RSV_INFO ( RSV_ID ,RSV_DT )
VALUES ( ( SELECT CONCAT('DR', LPAD(NEXTVAL(SEQ_BMS_DESTI_RSV_INFO_0), 8, '0')) AS RSV_ID ) ,NOW() );
INSERT INTO BMS_DESTI_RSV_RST_INFO ( RSV_ID ,MNG_ID ,COMPLETE_YN ) SELECT CONCAT('DR', LPAD(LASTVAL(SEQ_BMS_DESTI_RSV_INFO_0), 8, '0')) AS RSV_ID ,DVC.MNG_ID ,'N' COMPLETE_YN FROM BMS_VHC_MST VHC LEFT JOIN BMS_DVC_INFO DVC ON VHC.VHC_ID = DVC.VHC_ID LEFT JOIN BMS_DL_CD_INFO DL ON DVC.DVC_KIND = DL.DL_CD AND DL.CO_CD = 'DVC_KIND' WHERE DL.TXT_VAL1 = 'RD'
