mapper.select:
SELECT V.VHC_ID, V.VHC_NO, V.CHAS_NO, V.COMP_ID, T.COMP_NM, V.AREA, DATE_FORMAT(N.REG_DTM, '%Y%m%d%H%i') REG_DTM, DATE_FORMAT(N.RSV_DTM, '%Y%m%d%H%i') RSV_DTM, DATE_FORMAT(N.SEND_DTM, '%Y%m%d%H%i') SEND_DTM, DATE_FORMAT(N.UPD_DTM, '%Y%m%d%H%i') UPD_DTM, N.CONTENTS, N.UPD_ID, N.NOTICE_ID FROM BMS_NOTICE_MST N LEFT OUTER JOIN BMS_NOTICE_VHC_CMPSTN NV ON N.NOTICE_ID = NV.NOTICE_ID LEFT OUTER JOIN BMS_VHC_MST V ON V.VHC_ID = NV.VHC_ID LEFT OUTER JOIN BMS_TRANSCOMP_MST T ON V.COMP_ID = T.COMP_ID
N.NOTICE_ID = #{NOTICE_ID}
ORDER BY V.VHC_ID

SELECT C.STTN_ID, C.STTN_NM, C.STTN_NO, C.REMARK, C.GPS_X, C.GPS_Y, A.NOTICE_ID FROM BMS_NOTICE_MST A LEFT OUTER JOIN BMS_NOTICE_STTN_CMPSTN B ON A.NOTICE_ID = B.NOTICE_ID INNER JOIN BMS_STTN_MST C ON C.STTN_ID = B.STTN_ID AND C.DEL_YN!='Y'
A.NOTICE_ID = #{NOTICE_ID}
ORDER BY C.STTN_ID

mapper.delete:
DELETE FROM BMS_NOTICE_MST WHERE NOTICE_ID = #{NOTICE_ID};
DELETE FROM BMS_NOTICE_VHC_CMPSTN WHERE NOTICE_ID = #{NOTICE_ID};
DELETE FROM BMS_NOTICE_STTN_CMPSTN WHERE NOTICE_ID = #{NOTICE_ID};

mapper.update:
UPDATE BMS_NOTICE_MST SET TITLE = #{TITLE}, SEND_DIV = #{SEND_DIV}, 
SEND_STS = #{SEND_STS}, REG_DTM = STR_TO_DATE(#{REG_DTM}, '%Y%m%d%H%i'), RSV_DTM = STR_TO_DATE(#{RSV_DTM}, '%Y%m%d%H%i'), SEND_DTM = STR_TO_DATE(#{SEND_DTM}, '%Y%m%d%H%i'), UPD_DTM = #{UPD_DTM}, CONTENTS = #{CONTENTS}, UPD_ID = #{UPD_ID}
WHERE NOTICE_ID = #{NOTICE_ID}
