mapper.select:
select GRG_ID, SN, GPS_X, GPS_Y
from BMS_GRG_RDS_INFO
where GRG_ID = #{GRG_ID}

mapper.update:
 update BMS_GRG_RDS_INFO
	set GRG_ID = #{GRG_ID}, SN = #{SN}, GPS_X = #{GPS_X}, GPS_Y = #{GPS_Y}
	where GRG_ID = #{GRG_ID}

mapper.delete:
delete
from BMS_GRG_RDS_INFO
where GRG_ID = #{GRG_ID} and SN = #{SN}
