mapper.insert:
		INSERT INTO 		BMS_DRV_MST
						   (
							DRV_ID
							,ATTACH_ID
							,COMP_ID
							,DRV_NM
							,DRV_ENM
							,PHONE
							,BUS_DIV
							,EPLY_DATE1
							,EPLY_DATE2
							,LIC_NO
							,CERTI_DT
							,EPLY_YN
							,RETIRE_DT
							,REMARK
							,UPD_DTM
							,UPD_ID
							,USE_YN
						   )

		VALUES			   (
							#{DRV_ID}
							,#{ATTACH_ID}
							,#{COMP_ID}
							,#{DRV_NM}
							,#{DRV_ENM}
    						, AES_ENCRYPT(#{PHONE}, HEX(SHA2('TRACOM', 512)))							

							,#{BUS_DIV}
							,STR_TO_DATE(#{EPLY_DATE1}, '%Y%m%d')
							,STR_TO_DATE(#{EPLY_DATE2}, '%Y%m%d')
    						, AES_ENCRYPT(#{LIC_NO}, HEX(SHA2('TRACOM', 512)))								

							,STR_TO_DATE(#{CERTI_DT}, '%Y%m%d')
							,#{EPLY_YN}
							,STR_TO_DATE(#{RETIRE_DT}, '%Y%m%d')
							,#{REMARK}
							,#{UPD_DTM}
							,#{SSN_USER_ID}
							,#{USE_YN}
							)
