mapper.delete:
delete
from BMS_ROUT_MST
where ROUTE_ID = #{ROUTE_ID};

delete
from BMS_ROUT_COMP_CMPSTN
where ROUTE_ID = #{ROUTE_ID};

mapper.select:
 	select a.ROUTE_ID, a.NODE_ID, a.NODE_SN, a.NODE_NM, a.NODE_TYPE, a.STTN_ID, a.STTN_SN, a.CRS_ID, a.CRS_SN, a.ROUT_LEN
	from BMS_ROUT_NODE_CMPSTN a
	WHERE a.ROUTE_ID = #{ROUTE_ID}

mapper.delete:
delete from BMS_ROUT_COMP_CMPSTN
where ROUTE_ID = #{ROUTE_ID} 
and COMP_ID = #{COMP_ID}
