mapper.delete:
delete
from BMS_ROUT_MST
where ROUTE_ID = #{ROUTE_ID};

delete
from BMS_ROUT_COMP_CMPSTN
where ROUTE_ID = #{ROUTE_ID};

mapper.insert:
SELECT CONCAT('ND', LPAD(NEXTVAL(SEQ_BMS_NODE_MST_0), 8, '0'))
INSERT INTO BMS_NODE_MST (NODE_ID,NODE_NM,NODE_TYPE,GPS_X, GPS_Y,UPD_DTM,SSN_USER_ID) VALUES (#{NODE_ID}, #{NODE_NM}, #{NODE_TYPE}, #{GPS_X}, #{GPS_Y}, #{UPD_DTM}, #{SSN_USER_ID});
INSERT INTO BMS_ROUT_NODE_CMPSTN (ROUTE_ID,NODE_ID,NODE_SN,NODE_NM,NODE_TYPE,STTN_ID,STTN_NO,GPS_X,GPS_Y) VALUES(#{ROUTE_ID},#{NODE_ID},#{NODE_SN},#{NODE_NM},#{NODE_TYPE},#{STTN_ID},#{STTN_NO},#{GPS_X},#{GPS_Y});

mapper.delete:
delete from BMS_ROUT_COMP_CMPSTN
where ROUTE_ID = #{ROUTE_ID} 
and NODE_ID = #{NODE_ID}
