INSERT INTO BMS_NODE_MST (NODE_ID, NODE_NM,NODE_TYPE,GPS_X, GPS_Y,USE_YN,UPD_DTM,UPD_ID) VALUES (#{NODE_ID}, #{NODE_NM}, #{NODE_TYPE}, #{GPS_X}, #{GPS_Y}, 'Y', #{UPD_DTM}, #{SSN_USER_ID}) ON DUPLICATE KEY UPDATE NODE_NM = VALUES(NODE_NM) , NODE_TYPE = VALUES(NODE_TYPE) , GPS_X = VALUES(GPS_X) , GPS_Y = VALUES(GPS_Y) , USE_YN = 'Y' , UPD_DTM = VALUES(UPD_DTM) , UPD_ID = VALUES(UPD_ID);
INSERT INTO BMS_ROUT_NODE_CMPSTN (ROUT_ID,NODE_ID,NODE_SN,NODE_NM,NODE_TYPE,LINK_ID,STTN_ID,STTN_NO,GPS_X,GPS_Y,LINK_NODE_YN,UPD_DTM,UPD_ID) VALUES(#{ROUT_ID},#{NODE_ID},#{NODE_SN},#{NODE_NM},#{NODE_TYPE},#{LINK_ID},#{STTN_ID},#{STTN_NO},#{GPS_X},#{GPS_Y},'Y',#{UPD_DTM}, #{SSN_USER_ID});
INSERT INTO BMS_STTN_MST (STTN_ID,STTN_NM,AREA,STTN_NO,WAY_DIV,GPS_X,GPS_Y,USE_YN,DEL_YN,UPD_DTM,UPD_ID) VALUES(#{STTN_ID},#{NODE_NM},#{AREA},#{STTN_NO},#{WAY_DIV},#{GPS_X},#{GPS_Y},'Y','N',#{UPD_DTM},#{SSN_USER_ID}) ON DUPLICATE KEY UPDATE STTN_NM = VALUES(STTN_NM) , AREA = VALUES(AREA) , STTN_NO = VALUES(STTN_NO) , WAY_DIV = VALUES(WAY_DIV) , GPS_X = VALUES(GPS_X) , GPS_Y = VALUES(GPS_Y) , USE_YN = VALUES(USE_YN) , DEL_YN = VALUES(DEL_YN) , UPD_DTM = VALUES(UPD_DTM) , UPD_ID = VALUES(UPD_ID);
UPDATE BRT_MAIN_ROUT_NODE_INFO SET NODE_TYPE= #{NODE_TYPE} , NODE_NM= #{NODE_NM}, STTN_ID= #{STTN_ID}, STTN_NO= #{STTN_NO}, GPS_X= #{GPS_X}, GPS_Y= #{GPS_Y}, UPD_DTM= #{UPD_DTM}, UPD_ID= #{SSN_USER_ID} WHERE ROUT_ID= #{ROUT_ID} AND NODE_ID= #{NODE_ID} AND NODE_SN= #{NODE_SN};
