mapper.delete:
UPDATE BMS_STTN_MST
SET		USE_YN = 'N'
WHERE STTN_ID = #{STTN_ID};

UPDATE BMS_ROUT_NODE_CMPSTN
SET STTN_ID='', STTN_NO='', A.NODE_TYPE = 'NT03'
WHERE NODE_ID = #{STTN_ID};

UPDATE BMS_NODE_MST
SET NODE_TYPE = 'NT03'
WHERE NODE_ID = #{STTN_ID};

mapper.select:
select a.ROUT_ID, a.ROUT_NM, a.ROUT_TYPE, a.STTN_CNT, a.ROUT_LEN, a.CURVATURE, a.REMARK, b.STTN_ID
from BMS_ROUT_MST a INNER JOIN BMS_ROUT_NODE_CMPSTN b ON a.ROUT_ID = b.ROUT_ID AND b.NODE_TYPE != 'NT06'
where b.STTN_ID = #{STTN_ID}

mapper.delete:
delete from BMS_ROUT_NODE_CMPSTN
where ROUT_ID = #{ROUT_ID}
and STTN_ID = #{STTN_ID}
