mapper.update:
update BMS_CRS_MST set CRS_NM = #{CRS_NM}, CRS_KIND = #{CRS_KIND}, SIG_CTR_TYPE = #{SIG_CTR_TYPE}, USE_YN = #{USE_YN}, REMARK = #{REMARK}, UPD_DTM = #{UPD_DTM}, UPD_ID = #{SSN_USER_ID}, IMP_YN = #{IMP_YN}
where CRS_ID = #{CRS_ID};
update BMS_NODE_MST set NODE_NM = #{CRS_NM}, USE_YN = #{USE_YN}, UPD_DTM = #{UPD_DTM}, UPD_ID = #{SSN_USER_ID} where NODE_ID = #{CRS_ID}

mapper.delete:
UPDATE BMS_CRS_MST
SET	DEL_YN = 'Y', USE_YN = 'N'
WHERE CRS_ID = #{CRS_ID};

UPDATE BMS_ROUT_NODE_CMPSTN
	SET CRS_ID='', NODE_TYPE = 'NT003'
WHERE NODE_ID = #{CRS_ID};

UPDATE BMS_NODE_MST
	SET NODE_TYPE = 'NT003'
WHERE NODE_ID = #{CRS_ID};

mapper.select:
SELECT A.ROUT_ID, A.ROUT_NM, A.ROUT_TYPE, A.STTN_CNT, A.ROUT_LEN, A.CURVATURE, A.REMARK, B.CRS_ID
FROM BMS_ROUT_MST A INNER JOIN BMS_ROUT_NODE_CMPSTN B ON A.ROUT_ID = B.ROUT_ID AND A.DEL_YN !='Y' AND B.NODE_TYPE != 'NT006'
WHERE B.CRS_ID = #{CRS_ID}
