mapper.select:
SELECT DATE_FORMAT(A.ERROR_DT, '%Y-%m-%d') ERROR_DT ,A.REP_ROUT_ID ,A.ROUT_ID ,A.VHC_ID ,A.DVC_KIND ,SUM(A.JUDG_CNT) AS JUDG_CNT ,SUM(A.ERROR_CNT) AS ERROR_CNT ,B.VHC_NO FROM BRT_DVC_STAT A LEFT JOIN BMS_VHC_MST B ON A.VHC_ID = B.VHC_ID WHERE A.ROUT_ID = #{ROUT_ID} AND A.DVC_KIND = #{DVC_SELECT_DIV} AND A.VHC_ID IN
#{item}
AND DATE_FORMAT(A.ERROR_DT, '%Y%m%d') BETWEEN #{F_DATE} AND #{L_DATE}
GROUP BY ERROR_DT, A.VHC_ID ORDER BY A.VHC_ID, ERROR_DT

SELECT DATE_FORMAT(A.ERROR_DT, '%Y-%m-%d') ERROR_DT ,A.REP_ROUT_ID ,A.ROUT_ID ,A.VHC_ID ,A.DVC_KIND ,SUM(A.JUDG_CNT) AS JUDG_CNT ,SUM(A.ERROR_CNT) AS ERROR_CNT ,B.VHC_NO FROM BRT_DVC_STAT A LEFT JOIN BMS_VHC_MST B ON A.VHC_ID = B.VHC_ID WHERE A.ROUT_ID = #{ROUT_ID} AND A.DVC_KIND = #{DVC_SELECT_DIV} AND A.VHC_ID IN
#{item}
AND DATE_FORMAT(A.ERROR_DT, '%Y%m%d') BETWEEN #{F_DATE} AND #{L_DATE}
GROUP BY A.VHC_ID ORDER BY A.VHC_ID
