mapper.select:
SELECT A.OPER_DT ,A.REP_ROUT_ID ,A.ROUT_ID ,A.VHC_ID ,B.VHC_NO ,A.DRV_ID ,C.DRV_NM FROM BRT_OPER_PNCTLTY_STAT A LEFT JOIN BMS_VHC_MST B ON A.VHC_ID = B.VHC_ID LEFT JOIN BMS_DRV_MST C ON A.DRV_ID = C.DRV_ID WHERE A.ROUT_ID = #{ROUT_ID}
GROUP BY A.VHC_ID ORDER BY A.VHC_ID
GROUP BY A.DRV_ID ORDER BY A.DRV_ID
