mapper.insert:
INSERT INTO BRT_CUR_OPER_INFO (
	OPER_DT
	,REP_ROUT_ID
	,VHC_ID
	,ROUT_ID				
	,ALLOC_NO
	,OPER_SN							
	,NODE_ID
	,COR_ID
	,VHC_NO
	,DRV_ID
	,GPS_X
	,GPS_Y
	,GPS_X_RAW
	,GPS_Y_RAW
	,OPER_STS
	,DRV_ANGLE
	,CUR_SPD
	,LINK_ID
	,NODE_SN
	,NODE_TYPE
	,PRV_PLCE_NM
	,NEXT_NODE_ID
	,UPD_DTM
) 
VALUES (
	#{OPER_DT}


	,#{REP_ROUT_ID}
	,#{VHC_ID}
	,#{ROUT_ID}											

	,#{ALLOC_NO}
	,(SELECT FN_GET_OPER_SN(#{ROUT_ID}, #{NODE_ID}, VHC_ID, #{UPD_DTM})) 
	,#{NODE_ID}
	,#{COURSE_ID}
	,#{BUS_NO}
	,#{DRV_ID}
	,#{LONGITUDE}
	,#{LATITUDE}
	,#{LONGITUDE_RAW}
	,#{LATITUDE_RAW}			
	,(SELECT DL_CD FROM BMS_DL_CD_INFO WHERE CO_CD = 'OPER_STS' AND NUM_VAL4 = #{RUN_TYPE}) 
	,#{HEADING}
	,#{SPEED}
	,#{LINK_ID}
	,#{NODE_SN}
	,(SELECT NODE_TYPE FROM BMS_NODE_MST WHERE NODE_ID = #{NODE_ID})
	,(IF(NODE_TYPE = 'NT001' OR NODE_TYPE = 'NT002', (SELECT NODE_NM FROM BMS_NODE_MST WHERE NODE_ID = #{NODE_ID}), NULL))
	,#{NEXT_NODE_ID}
	,#{UPD_DTM}
)
ON DUPLICATE KEY UPDATE
	,ROUT_ID = VALUES(ROUT_ID)
	,ALLOC_NO = VALUES(ALLOC_NO)
	,OPER_SN = VALUES(OPER_SN)	
	,NODE_ID = VALUES(NODE_ID)
	,COR_ID = VALUES(COR_ID)
	,VHC_NO = VALUES(VHC_NO)
	,DRV_ID = VALUES(DRV_ID)
	,GPS_X = VALUES(GPS_X)
	,GPS_Y = VALUES(GPS_Y)
	,OPER_STS = VALUES(OPER_STS)
	,DRV_ANGLE = VALUES(DRV_ANGLE)
	,CUR_SPD = VALUES(CUR_SPD)
	,LINK_ID = VALUES(LINK_ID)
	,NODE_SN = VALUES(NODE_SN)
	,NODE_TYPE = VALUES(NODE_TYPE)
	,PRV_PLCE_NM = VALUES(PRV_PLCE_NM)	
	,UPD_DTM = VALUES(UPD_DTM);

mapper.select:
SELECT
	OPER_DT,
	REP_ROUT_ID,
	ALLOC_NO,
	WAY_DIV,
	VHC_ID,
	OPER_VHC_ID
FROM BRT_CUR_ALLOC_PL_INFO
WHERE OPER_DT = #{OPER_DT}
	AND REP_ROUT_ID = #{ROUT_ID}
	AND WAY_DIV = #{WAY_DIV}
	AND VHC_ID = #{VHC_ID}
LIMIT 0, 1

mapper.update:
UPDATE BRT_CUR_ALLOC_PL_INFO SET OPER_VHC_ID = '', UPD_DTM = NOW()
WHERE OPER_DT = #{OPER_DT} AND ALLOC_NO != #{ALLOC_NO} AND OPER_VHC_ID = #{OPER_VHC_ID};

UPDATE BRT_CUR_ALLOC_PL_INFO SET OPER_VHC_ID = #{OPER_VHC_ID}, UPD_DTM = NOW()
WHERE OPER_DT = #{OPER_DT} AND REP_ROUT_ID = #{REP_ROUT_ID} AND ALLOC_NO = #{ALLOC_NO};

mapper.insert:
INSERT INTO BRT_INCDNT_INFO ( INCDNT_ID ,OPER_DT ,REP_ROUT_ID ,VHC_ID ,INCDNT_TYPE ,ROUT_ID ,ALLOC_NO ,OPER_SN ,COR_ID ,OCR_DTM ,VHC_NO ,DRV_ID ,REP_ROUT_NM ,ROUT_NM ,LINK_ID ,NODE_ID ,GPS_X ,GPS_Y ,UPD_DTM ) VALUES ( (SELECT CONCAT('IC', LPAD(NEXTVAL(SEQ_BRT_INCDNT_INFO_0), 8, '0')) AS 'INCDNT_ID') ,#{OPER_DT}
,#{REP_ROUT_ID} ,#{VHC_ID} ,(SELECT DL_CD FROM BMS_DL_CD_INFO WHERE CO_CD = 'INCDNT_TYPE' AND NUM_VAL4 = #{EVENT_CD}) ,#{ROUT_ID}
,#{ALLOC_NO} ,(SELECT FN_GET_OPER_SN(#{ROUT_ID}, #{NODE_ID}, VHC_ID, #{UPD_DTM})) ,#{COURSE_ID} ,#{UPD_DTM} ,#{BUS_NO} ,#{DRV_ID} ,(SELECT REP_ROUT_NM FROM BMS_ROUT_MST WHERE ROUT_ID = #{ROUT_ID}) ,(SELECT ROUT_NM FROM BMS_ROUT_MST WHERE ROUT_ID = #{ROUT_ID}) ,#{LINK_ID} ,#{NODE_ID} ,#{LONGITUDE} ,#{LATITUDE} ,NOW() ) ON DUPLICATE KEY UPDATE ROUT_ID = VALUES(ROUT_ID) ,ALLOC_NO = VALUES(ALLOC_NO) ,OPER_SN = VALUES(OPER_SN) ,COR_ID = VALUES(COR_ID) ,OCR_DTM = VALUES(OCR_DTM) ,VHC_NO = VALUES(VHC_NO) ,DRV_ID = VALUES(DRV_ID) ,REP_ROUT_NM = VALUES(REP_ROUT_NM) ,ROUT_NM = VALUES(ROUT_NM) ,LINK_ID = VALUES(LINK_ID) ,NODE_ID = VALUES(NODE_ID) ,GPS_X = VALUES(GPS_X) ,GPS_Y = VALUES(GPS_Y) ,UPD_DTM = VALUES(UPD_DTM)
