mapper.insert:
INSERT INTO BRT_DSPTCH_LOG (
	SEND_DATE
	,OPER_DT
	,REP_ROUT_ID
	,VHC_ID
	,ROUT_ID				
	,ALLOC_NO
	,OPER_SN		
	,COR_ID
	,REP_ROUT_NM
	,ROUT_NM			
	,VHC_NO
	,LINK_ID
	,NODE_ID
	,DSPTCH_DIV
	,DSPTCH_KIND
	,SEND_GPS_X
	,SEND_GPS_Y
	,DSPTCH_CONTS
) 
VALUES (
	#{SEND_DATE}
	,DATE_FORMAT(#{SEND_DATE}, '%Y-%m-%d')
	,#{REP_ROUT_ID}			
	,#{VHC_ID}
	,#{ROUT_ID}											
	,#{ALLOC_NO}
	,#{OPER_SN}
	,#{COR_ID}
	,(SELECT REP_ROUT_NM FROM BMS_REP_ROUT_MST WHERE REP_ROUT_ID = #{REP_ROUT_ID})	
	,(SELECT ROUT_NM FROM BMS_ROUT_MST WHERE ROUT_ID = #{ROUT_ID})
	,#{VHC_NO}
	,#{LINK_ID}
	,#{NODE_ID}
	,#{DSPTCH_DIV}
	,#{DSPTCH_KIND}			
	,#{GPS_X}
	,#{GPS_Y}
	,#{DSPTCH_CONTS}
)

INSERT INTO BRT_OPER_EVENT_HIS (
	OPER_DT
	,REP_ROUT_ID
	,VHC_ID
	,UPD_DTM
	,VHC_NO
	,DRV_ID
	,COR_ID
	,ROUT_ID
	,GPS_X_RAW
	,GPS_Y_RAW
	,DRV_ANGLE
	,GPS_X
	,GPS_Y
	,CUR_SPD
	,LINK_ID
	,NODE_ID
	,NODE_SN
	,OPER_STS
	,EVT_TYPE
	,EVT_DATA
	,STOP_TM
	,OCR_DTM
) 
VALUES (
	DATE_FORMAT(#{UPD_DTM}, '%Y-%m-%d')
	,(SELECT REP_ROUT_ID FROM BMS_ROUT_MST WHERE ROUT_ID = #{ROUT_ID})			
	,(SELECT VHC_ID FROM BMS_VHC_MST WHERE VHC_NO = #{BUS_NO})
	,NOW()
	,#{BUS_NO}
	,#{DRV_ID}
	,#{COURSE_ID}
	,#{ROUT_ID}
	,#{LONGITUDE_RAW}
	,#{LATITUDE_RAW}
	,#{HEADING}
	,#{LONGITUDE}
	,#{LATITUDE}
	,#{SPEED}
	,#{LINK_ID}
	,#{NODE_ID}
	,#{NODE_SN}
	,(SELECT DL_CD FROM BMS_DL_CD_INFO WHERE CO_CD = 'VHC_OPER_TYPE' AND NUM_VAL4 = #{RUN_TYPE}) 
	,(SELECT DL_CD FROM BMS_DL_CD_INFO WHERE CO_CD = 'OPER_EVT_TYPE' AND NUM_VAL4 = #{EVENT_CD}) 
	,#{EVENT_DATA} 
	,#{STOP_TM}
	,#{UPD_DTM}
)
