mapper.update:
UPDATE			BMS_DVC_INFO
SET				VHC_ID = #{VHC_ID}
				,ORG_ID = #{ORG_ID}
				,DVC_KIND = #{DVC_KIND}
				,DVC_COORDS = #{DVC_COORDS}
				,INST_LOC = #{INST_LOC}
				,DVC_IP = #{DVC_IP}
				,MNG_ID = #{MNG_ID}	
				,USE_YN = #{USE_YN}
				,CTR_YN = #{CTR_YN}
				,REMARK = #{REMARK}
				,UPD_DTM = #{UPD_DTM}
				,UPD_ID = #{SSN_USER_ID}
				,CLSFCTN_ID	= #{CLSFCTN_ID}

WHERE			DVC_ID = #{DVC_ID}

mapper.delete:
DELETE FROM PLF_IMP_DEV_MST
WHERE IMP_ID = #{MNG_ID};

DELETE FROM PLF_IMP_SESS_INFO
WHERE SESS_ID = #{MNG_ID};
