mapper.select:
SELECT VM.VHC_NO ,(IF(UL.COMPLETE_YN IS NULL, '', IF(UL.COMPLETE_YN = 'N', '예약중', ''))) AS COMPLETE_YN ,date_format(URI.RSV_DT, '%Y-%m-%d') as RSV_DT ,UL.PROCE_RST ,date_format(UL.SEND_DTM,'%Y-%m-%d') as SEND_DTM ,DI.MNG_ID ,DI.ORG_ID ,OM.ORG_DIV ,DI.DVC_KIND ,DI.INST_LOC FROM BMS_DVC_INFO DI LEFT JOIN BMS_VHC_MST VM ON DI.VHC_ID = VM.VHC_ID LEFT JOIN BMS_UPD_LOG UL ON DI.MNG_ID = UL.MNG_ID LEFT JOIN BMS_ORG_MST OM ON DI.ORG_ID = OM.ORG_ID LEFT JOIN BMS_UPD_RSV_INFO URI ON UL.RSV_ID = URI.RSV_ID WHERE DI.VHC_ID = #{VHC_ID}
AND DI.DVC_KIND = #{DVC_KIND}
AND DATE_FORMAT(URI.RSV_DT, '%Y%m') = #{SELC_REV_DATE}
ORDER BY DI.DVC_KIND

mapper.insert:


    		SELECT CONCAT('UR', LPAD(NEXTVAL(SEQ_BMS_UPD_LOG_0), 8, '0')) AS RSV_ID

		insert into BMS_UPD_LOG
				(
					RSV_ID
					,MNG_ID
					,PROCE_RST
					,COMPLETE_YN
					,REMARK
				)	

		values (
					#{RSV_ID}
					,#{MNG_ID}
					,#{PROCE_RST}
					,'N'
					,#{REMARK}
				)


    		SELECT CONCAT('UR', LPAD(LASTVAL(SEQ_BMS_UPD_LOG_0), 8, '0')) AS RSV_ID

		insert into BMS_UPD_RSV_INFO
				(
					RSV_ID
					,MNG_ID
					,RSV_DT
					,ATTACH_ID
					,VER_INFO
					,UPD_DTM
					,UPD_ID
				)
		values	(
					#{RSV_ID}
					,#{MNG_ID}
					,#{RSV_DT}
					,#{ATTACH_ID}
					,#{VER_INFO}
					,#{UPD_DTM}
					,#{UPD_ID}
				)

mapper.update:
UPDATE 
	BMS_DVC_INFO
SET
	FILE_NM = #{FILE_NM}
WHERE
	MNG_ID = #{MNG_ID}
