mapper.update:
UPDATE			BMS_DVC_HIS
SET				APLY_DATE = STR_TO_DATE(#{APLY_DATE}, '%Y%m%d')
				,WORK_TYPE = #{WORK_TYPE}
				,WORK_AMT = #{WORK_AMT}
				,REMARK = #{REMARK}
				,UPD_DTM = #{UPD_DTM}
				,UPD_ID = #{SSN_USER_ID}

WHERE			DVC_ID = #{DVC_ID}
AND				SN = #{SN}

mapper.delete:
DELETE FROM 	BMS_DVC_HIS
WHERE			DVC_ID = #{DVC_ID}
AND				SN = #{SN}
