mapper.select:
SELECT DATE_FORMAT(A.SEND_DATE, '%Y-%m-%d %H:%i:%s') SEND_DATE ,A.REP_ROUT_ID ,A.VHC_NO ,A.VHC_ID ,A.SEND_GPS_X AS GPS_X ,A.SEND_GPS_Y AS GPS_Y ,A.DSPTCH_DIV ,A.DSPTCH_KIND
,CASE WHEN A.DSPTCH_DIV = 'DP002' THEN CASE WHEN A.DSPTCH_CONTS>0 THEN TIME_FORMAT(SEC_TO_TIME(A.DSPTCH_CONTS), '%i분 %s초 느립니다.') WHEN A.DSPTCH_CONTS<0 THEN TIME_FORMAT(SEC_TO_TIME(A.DSPTCH_CONTS), '%i분 %s초 빠릅니다.') ELSE '정상 운행중입니다.' END WHEN A.DSPTCH_DIV = 'DP003' THEN TIME_FORMAT(SEC_TO_TIME(A.DSPTCH_CONTS), '%i분 %s초 빠릅니다.') ELSE A.DSPTCH_CONTS END AS DSPTCH_CONTS
,A.ROUT_ID ,A.LINK_ID ,A.NODE_ID ,A.REP_ROUT_ID ,B.NODE_TYPE ,B.NODE_NM ,C.ROUT_NM ,D.LINK_NM ,E.REP_ROUT_NM ,F.DRV_NM FROM BRT_DSPTCH_LOG A LEFT JOIN BMS_NODE_MST B ON A.NODE_ID = B.NODE_ID LEFT JOIN BMS_ROUT_MST C ON A.ROUT_ID = C.ROUT_ID LEFT JOIN BMS_LINK_MST D ON A.LINK_ID = D.LINK_ID LEFT JOIN BMS_REP_ROUT_MST E ON A.REP_ROUT_ID = E.REP_ROUT_ID LEFT JOIN BMS_DRV_MST F ON A.DRV_ID = F.DRV_ID
A.REP_ROUT_ID = #{CONTENT1}
AND A.ROUT_ID = #{CONTENT2}
AND A.VHC_NO LIKE CONCAT('%', #{CONTENT3}, '%')
AND DATE_FORMAT(A.SEND_DATE, '%Y%m%d') >= #{F_DATE}
AND DATE_FORMAT(A.SEND_DATE, '%Y%m%d') <= #{L_DATE}
AND DATE_FORMAT(A.SEND_DATE, '%Y%m%d') BETWEEN #{F_DATE} AND #{L_DATE}
AND DSPTCH_DIV = #{DSPTCH_DIV}
ORDER BY A.SEND_DATE DESC
