mapper.select:
SELECT DATE_FORMAT(A.OPER_DT, '%Y-%m-%d') OPER_DT ,DATE_FORMAT(A.OCR_DTM, '%Y-%m-%d %H:%i:%s') OCR_DTM ,A.REP_ROUT_ID ,A.ROUT_ID ,A.OPER_SN ,A.ALLOC_NO ,A.VHC_NO ,A.CHG_OPER_DIV ,A.OCR_NODE_ID ,A.OCR_LINK_ID ,A.GPS_X ,A.GPS_Y ,B.REP_ROUT_NM ,C.ROUT_NM ,COUNT(A.OPER_DT) AS CHG_CNT FROM BRT_CUR_CHG_OPER_INFO_HIS A LEFT OUTER JOIN BMS_REP_ROUT_MST B ON A.REP_ROUT_ID = B.REP_ROUT_ID LEFT OUTER JOIN BMS_ROUT_MST C ON A.ROUT_ID = C.ROUT_ID
A.OCR_NODE_TYPE = 'NT002'
AND A.REP_ROUT_ID = #{CONTENT1}
AND A.ROUT_ID = #{CONTENT2}
AND A.VHC_NO LIKE CONCAT('%', #{CONTENT3}, '%')
AND DATE_FORMAT(A.OPER_DT, '%Y%m%d') >= #{F_DATE_VH0200}
AND DATE_FORMAT(A.OPER_DT, '%Y%m%d') <= #{L_DATE_VH0200}
AND DATE_FORMAT(A.OPER_DT, '%Y%m%d') BETWEEN #{F_DATE_VH0200} AND #{L_DATE_VH0200}
GROUP BY A.OPER_DT, A.ALLOC_NO ORDER BY A.OPER_DT DESC, A.OCR_DTM DESC

SELECT A.REP_ROUT_ID ,A.OPER_SN ,A.NODE_ID ,A.NODE_SN ,A.ALLOC_NO ,A.COR_ID ,A.NODE_TYPE ,A.ARRV_TM AS CHG_ARRV_TM ,A.DPRT_TM AS CHG_DPRT_TM ,DATE_FORMAT(A.UPD_DTM, '%Y-%m-%d') UPD_DTM ,DATE_FORMAT(A.OCR_DTM, '%Y-%m-%d') OCR_DTM ,DATE_FORMAT(A.OPER_DT, '%Y-%m-%d') OPER_DT ,C.ROUT_ID ,C.NODE_NM ,C.ACCRU_LEN FROM BRT_CUR_CHG_OPER_DTL_INFO_HIS A INNER JOIN BMS_ROUT_NODE_CMPSTN C ON A.ROUT_ID = C.ROUT_ID AND A.NODE_ID = C.NODE_ID AND A.NODE_SN = C.NODE_SN AND C.NODE_TYPE = 'NT002' WHERE DATE_FORMAT(A.OCR_DTM, '%Y-%m-%d') = #{OCR_DTM} AND C.ROUT_ID = #{ROUT_ID}
AND A.OPER_SN = #{OPER_SN}
ORDER BY OPER_SN, NODE_SN
