mapper.update:
UPDATE	BRT_OPER_VIOLT_HIS
SET		OCR_DTM		= #{OCR_DTM}
		,NODE_ID	= #{NODE_ID}
		,VIOLT_TYPE	= #{VIOLT_TYPE}
		,GPS_X		= #{GPS_X}
		,GPS_Y		= #{GPS_Y}
		,JUDGE		= #{JUDGE}
		,JUDG_ORG	= #{JUDG_ORG}
		,JUDG_TYPE	= #{JUDG_TYPE}
		,JUDG_DESC	= #{JUDG_DESC}
		,UPD_DTM	= NOW()
WHERE
		VHC_ID = #{VHC_ID}
AND
		ROUT_ID = #{ROUT_ID}
AND
		OCR_DTM = #{OCR_DTM}

mapper.delete:
DELETE FROM 
	BRT_OPER_VIOLT_HIS
WHERE 
	VHC_ID = #{VHC_ID}
AND
	ROUT_ID = #{ROUT_ID}
AND
	OCR_DTM = #{OCR_DTM}
