mapper.insert:


    		SELECT CONCAT('CI', LPAD(NEXTVAL(SEQ_BRT_CPLNT_HIS_0), 8, '0')) AS CPLNT_ID


		INSERT INTO BRT_CPLNT_HIS 
			(
				CPLNT_ID
				,OCR_DTM
				,ROUT_NM
				,VHC_NO
				,CPLNT_TYPE
				,GPS_X
				,GPS_Y
				,CPLNT_CONTS
				,JUDGE
				,JUDG_ORG
				,JUDG_TYPE
				,JUDG_DESC	
				,UPD_DTM	
			)
		VALUES
			(
				#{CPLNT_ID}
				,#{OCR_DTM}
				,#{ROUT_NM}
				,#{VHC_NO}
				,#{CPLNT_TYPE}
				,#{GPS_X}
				,#{GPS_Y}
				,#{CPLNT_CONTS}
				,#{JUDGE}
				,#{JUDG_ORG}
				,#{JUDG_TYPE}
				,#{JUDG_DESC}
				,#{UPD_DTM}
			)

mapper.update:
UPDATE 
	BRT_CPLNT_HIS
SET
	OCR_DTM = STR_TO_DATE(#{OCR_DTM}, '%Y-%m-%d')
	,ROUT_NM = #{ROUT_NM}
	,VHC_NO = #{VHC_NO}
	,CPLNT_TYPE = #{CPLNT_TYPE}
	,GPS_X = #{GPS_X}
	,GPS_Y = #{GPS_Y}
	,CPLNT_CONTS = #{CPLNT_CONTS}
	,JUDGE = #{JUDGE}
	,JUDG_ORG = #{JUDG_ORG}
	,JUDG_TYPE = #{JUDG_TYPE}
	,JUDG_DESC = #{JUDG_DESC}
	,UPD_DTM = #{UPD_DTM}
WHERE
	CPLNT_ID = #{CPLNT_ID}
