mapper.insert:


    		SELECT CONCAT('CI', LPAD(NEXTVAL(SEQ_BRT_CPLNT_HIS_0), 8, '0')) AS CPLNT_ID


		INSERT INTO BRT_CPLNT_HIS 
			(
				CPLNT_ID
				,ROUT_ID
				,NODE_ID
				,REP_ROUT_ID
				,REP_ROUT_NM
				,OCR_DTM
				,ROUT_NM
				,VHC_NO
				,CPLNT_TYPE
				,CPLNT_CONTS
				,JUDGE
				,JUDG_ORG
				,JUDG_TYPE
				,JUDG_DESC	
				,UPD_DTM
				,OPER_DT
				,ALLOC_NO
				,OPER_SN
				,CPLNT_NM
				,CPLNT_PHONE
				,JUDG_DTM
				,VHC_ID

			)
		VALUES
			(
				#{CPLNT_ID}
				,#{ROUT_ID}
				,#{NODE_ID}
				,#{REP_ROUT_ID}
				,#{REP_ROUT_NM}
				,#{OCR_DTM}
				,#{ROUT_NM}
				,#{VHC_NO}
				,#{CPLNT_TYPE}
				,#{CPLNT_CONTS}
				,#{JUDGE}
				,#{JUDG_ORG}
				,#{JUDG_TYPE}
				,#{JUDG_DESC}
				,#{UPD_DTM}
				,STR_TO_DATE(#{OPER_DT}, '%Y%m%d')
				,#{ALLOC_NO}
				,#{OPER_SN}
				,#{CPLNT_NM}
				,AES_ENCRYPT(#{CPLNT_PHONE}, HEX(SHA2('TRACOM', 512)))	
				,STR_TO_DATE(#{JUDG_DTM}, '%Y%m%d')
				,#{VHC_ID}

			)

mapper.update:
UPDATE 
	BRT_CPLNT_HIS
SET
	OCR_DTM = #{OCR_DTM}
	,ROUT_NM = #{ROUT_NM}
	,ROUT_ID = #{ROUT_ID}
	,REP_ROUT_NM = #{REP_ROUT_NM}
	,REP_ROUT_ID = #{REP_ROUT_ID}
	,NODE_ID = #{NODE_ID}
	,VHC_NO = #{VHC_NO}
	,VHC_ID = #{VHC_ID}
	,CPLNT_TYPE = #{CPLNT_TYPE}
	,CPLNT_CONTS = #{CPLNT_CONTS}
	,JUDGE = #{JUDGE}
	,JUDG_ORG = #{JUDG_ORG}
	,JUDG_TYPE = #{JUDG_TYPE}
	,JUDG_DESC = #{JUDG_DESC}
	,UPD_DTM = #{UPD_DTM}
	,OPER_DT = #{OPER_DT}
	,CPLNT_NM = #{CPLNT_NM}
	,CPLNT_PHONE = AES_ENCRYPT(#{CPLNT_PHONE}, HEX(SHA2('TRACOM', 512)))
	,JUDG_DTM = #{JUDG_DTM}
WHERE
	CPLNT_ID = #{CPLNT_ID}
