mapper.update:
UPDATE BMS_AUTHORITY_MST
   SET AUTH_NM = #{AUTH_NM}, SYSTEM_BIT = #{SYSTEM_BIT}, USE_YN = #{USE_YN}
       , UPD_DTM = #{UPD_DTM}, UPD_ID = #{SSN_USER_ID}
 WHERE AUTH_CD = #{AUTH_CD}

mapper.delete:
DELETE FROM BMS_USER_AUTH_CMPSTN 


		 AND USER_ID = #{USER_ID}


		 AND AUTH_CD = #{AUTH_CD}
