mapper.select:
SELECT A.CO_CD, A.DL_CD, A.DL_CD_NM, A.SORT FROM BMS_DL_CD_INFO A, BMS_CO_CD_INFO B WHERE B.CO_CD = A.CO_CD AND B.USE_YN = 'Y' AND A.USE_YN = 'Y' AND A.CO_CD IN
#{item}
ORDER BY A.CO_CD, A.SORT

SELECT DISTINCT PA.PROG_CD, PA.AUTH_CHECK, PA.SAV_AH, PA.EXL_AH
  FROM BMS_USER_AUTH_CMPSTN UA INNER JOIN BMS_PROG_AUTH_INFO PA ON UA.AUTH_CD = PA.AUTH_CD
 WHERE UA.USER_ID = #{SSN_USER_ID}

mapper.update:
UPDATE BMS_CO_CD_INFO 
   SET CO_CD = #{CO_CD},CO_CD_NM = #{CO_CD_NM}, SORT = #{SORT}, USE_YN = #{USE_YN}, REMARK = #{REMARK}
 WHERE CO_CD = #{CO_CD_ORG}

UPDATE BMS_DL_CD_INFO 
   SET DL_CD = #{DL_CD}, DL_CD_NM = #{DL_CD_NM}, SORT = #{SORT}, USE_YN = #{USE_YN}, TXT_VAL1 = #{TXT_VAL1}
	 , TXT_VAL2 = #{TXT_VAL2}, TXT_VAL3 = #{TXT_VAL3}, REMARK = #{REMARK}
 WHERE CO_CD = #{CO_CD}
   AND DL_CD = #{DL_CD_ORG}

UPDATE BM_MAIN_SETTING
   SET MAIN_LAYOUT_PAGE_CODE = #{MAIN_LAYOUT_PAGE_CODE},
	   USE_YN_SHORTCUT = #{USE_YN_SHORTCUT}
 WHERE USER_ID = #{USER_ID}

mapper.select:
SELECT MAIN_LAYOUT_PAGE_CODE, USE_YN_SHORTCUT 
  FROM BM_MAIN_SETTING 
 WHERE USER_ID = #{USER_ID}

SELECT SYSTEM_CD, SYSTEM_VALUE, SYSTEM_NM FROM BMS_SYSTEM_MST WHERE SYSTEM_CD IN
#{item}
ORDER BY SYSTEM_CD, SYSTEM_VALUE
