#{item}
ORDER BY A.CO_CD, A.SORT

SELECT CO_CD, DL_CD, DL_CD_NM, SORT, USE_YN, TXT_VAL1, TXT_VAL2, TXT_VAL3, REMARK, NUM_VAL4, NUM_VAL5, NUM_VAL6
  FROM BMS_DL_CD_INFO A
 WHERE A.CO_CD = #{CO_CD} 

	AND DL_CD = #{DL_CD}

 ORDER BY A.SORT

