mapper.select:
SELECT AUTH_CD 
  FROM BMS_USER_AUTH_CMPSTN 
 WHERE USER_ID = #{USER_ID}

SELECT HPB.USER_ID, HOB.ORG_NM, HPB.SYSTEM_BIT, HPB.USER_NM, HPB.EMAIL, HPB.HP_NO ,HPB.USE_YN,HPB.REMARK FROM BMS_USER_MST HPB LEFT OUTER JOIN BMS_ORG_MST HOB ON HPB.ORG_ID = HOB.ORG_ID
AND HPB.USER_ID = #{USER_ID}
AND HPB.USER_NM LIKE CONCAT( '%', #{USER_NM}, '%' )

mapper.insert:
insert into BMS_USER_MST( USER_ID,USER_NM,ORG_ID,SYSTEM_BIT,USER_PS,EMAIL,HP_NO, USE_YN,REMARK,UPD_DTM,UPD_ID,JOB_POSITION,JOB_DUTY )
values( #{USER_ID},#{USER_NM},#{ORG_ID},#{SYSTEM_BIT},#{USER_PS}, #{EMAIL},#{HP_NO},
'Y',#{REMARK},#{UPD_DTM},#{SSN_USER_ID},#{JOB_POSITION},#{JOB_DUTY} );

insert into BMS_USER_AUTH_CMPSTN (USER_ID, AUTH_CD)
values(#{USER_ID}, #{AUTH_CD});

mapper.update:
UPDATE BMS_USER_MST
   SET USER_NM = #{USER_NM}, ORG_ID = #{ORG_ID}, SYSTEM_BIT = #{SYSTEM_BIT}, 

   USER_PS = #{USER_PS}

	 , EMAIL = #{EMAIL}, HP_NO = #{HP_NO}, USE_YN = #{USE_YN}, REMARK = #{REMARK}
	 , UPD_DTM = #{UPD_DTM}, UPD_ID = #{SESSION_USER_ID}
	 , JOB_POSITION = #{JOB_POSITION}, JOB_DUTY = #{JOB_DUTY}
 WHERE USER_ID = #{USER_ID};

 UPDATE BMS_USER_AUTH_CMPSTN
 	SET	AUTH_CD = #{AUTH_CD}
  WHERE USER_ID = #{USER_ID};
