        )

mapper.select:
SELECT A.REP_ROUT_ID, A.DAY_DIV, A.WAY_DIV, A.OPER_SN, A.ROUT_ID, A.ROUT_ST_TM, A.ROUT_ED_TM, B.REP_ROUT_NM, C.ROUT_NM FROM BRT_OPER_PL_ROUT_INFO A INNER JOIN BMS_REP_ROUT_MST B ON A.REP_ROUT_ID = B.REP_ROUT_ID LEFT OUTER JOIN BMS_ROUT_MST C ON A.ROUT_ID = C.ROUT_ID WHERE A.REP_ROUT_ID = #{REP_ROUT_ID} AND A.DAY_DIV = #{DAY_DIV}
AND A.WAY_DIV = #{WAY_DIV}
AND (A.REP_ROUT_ID LIKE CONCAT('%',#{CONTENT},'%') || B.REP_ROUT_NM LIKE CONCAT('%',#{CONTENT},'%'))
AND A.REP_ROUT_ID LIKE CONCAT('%',#{CONTENT},'%')
AND B.REP_ROUT_NM LIKE CONCAT('%',#{CONTENT},'%')

SELECT A.REP_ROUT_ID ,A.DAY_DIV ,A.WAY_DIV ,A.OPER_SN ,A.NODE_ID ,A.NODE_SN ,A.ALLOC_NO ,A.COR_ID ,A.NODE_TYPE ,A.ARRV_TM ,A.DPRT_TM
,A.UPD_DTM ,A.UPD_ID ,B.REP_ROUT_NM ,B.WAY_ASC_NM ,B.WAY_DESC_NM ,C.ROUT_ID ,C.NODE_NM ,C.ACCRU_LEN FROM BRT_OPER_ALLOC_PL_NODE_INFO A INNER JOIN BMS_REP_ROUT_MST B ON A.REP_ROUT_ID = B.REP_ROUT_ID RIGHT OUTER JOIN BMS_ROUT_NODE_CMPSTN C ON A.ROUT_ID = C.ROUT_ID AND A.NODE_ID = C.NODE_ID AND A.NODE_SN = C.NODE_SN
AND C.NODE_TYPE = #{NODE_TYPE}
WHERE A.DAY_DIV = #{DAY_DIV} AND C.ROUT_ID = #{ROUT_ID}
AND A.OPER_SN = #{OPER_SN}
AND A.NODE_SN = #{NODE_SN}
ORDER BY OPER_SN, NODE_SN

SELECT A.REP_ROUT_ID ,A.DAY_DIV ,A.ALLOC_NO ,A.COR_SN ,A.WAY_DIV ,A.COR_ID ,B.COR_NM ,B.COR_TYPE ,B.MOVE_TM ,B.COR_COLOR ,C.ROUT_ID ,C.ROUT_SN ,C.REST_TM ,D.REP_ROUT_NM ,D.ROUT_NM FROM BRT_OPER_ALLOC_PL_COR_INFO A INNER JOIN BRT_COR_MST B ON A.COR_ID = B.COR_ID LEFT OUTER JOIN BRT_COR_DTL_INFO C ON A.COR_ID = C.COR_ID INNER JOIN BMS_ROUT_MST D ON A.REP_ROUT_ID = D.REP_ROUT_ID AND C.ROUT_ID = D.ROUT_ID WHERE A.REP_ROUT_ID = #{REP_ROUT_ID}
AND A.DAY_DIV = #{DAY_DIV}
AND A.DAY_DIV = #{ALLOC_NO}
AND A.WAY_DIV = #{WAY_DIV}
ORDER BY A.REP_ROUT_ID, A.DAY_DIV, A.ALLOC_NO, A.COR_SN ,C.ROUT_SN ASC

SET @ROWNUM = 0; SELECT A.COR_ID , A.REST_TM , @ROWNUM:=@ROWNUM+1 AS ROUT_SN , A.COR_DTL_COLOR , B.COR_COLOR , B.COR_NM , B.COR_TYPE , C.ROUT_ID , C.ROUT_NM , C.ROUT_TYPE , C.STTN_CNT , C.ROUT_LEN , C.CURVATURE , C.JIT_DSPCH_YN , C.ST_STTN_NM , C.ED_STTN_NM , C.WAY_DIV FROM BRT_COR_DTL_INFO A INNER JOIN BRT_COR_MST B ON A.COR_ID = B.COR_ID INNER JOIN BMS_ROUT_MST C ON A.ROUT_ID = C.ROUT_ID
WHERE A.COR_ID IN
#{item}
WHERE A.COR_ID = #{COR_ID}
ORDER BY A.ROUT_SN ASC
