mapper.update:

UPDATE BMS_ROUT_NODE_CMPSTN


	GPS_X =#{GPS_X},


	GPS_Y =#{GPS_Y},


	UPD_DTM = #{UPD_DTM},


	UPD_ID = #{SSN_USER_ID}


WHERE ROUT_ID != #{ROUT_ID}

	AND NODE_TYPE = 'NT001' AND NODE_ID = #{CRS_ID}


	AND NODE_TYPE = 'NT002' AND NODE_ID = #{STTN_ID}


UPDATE BRT_MAIN_ROUT_NODE_INFO


	GPS_X =#{GPS_X},


	GPS_Y =#{GPS_Y},


	UPD_DTM = #{UPD_DTM},


	UPD_ID = #{SSN_USER_ID}


WHERE ROUT_ID != #{ROUT_ID}

	AND NODE_TYPE = 'NT001' AND NODE_ID = #{CRS_ID}


	AND NODE_TYPE = 'NT002' AND NODE_ID = #{STTN_ID}

mapper.select:
SELECT ROUT_ID, REP_ROUT_ID, REP_ROUT_NM, INT_ROUT_ID, ROUT_NM, ROUT_TYPE, ST_STTN_ID, ST_STTN_NM, ED_STTN_ID, ED_STTN_NM, RET_STTN_ID, RET_STTN_NM, ROUT_LEN, WAY_DIV, USER_WAY_DIV FROM BMS_ROUT_MST WHERE DEL_YN != 'Y'
AND REP_ROUT_ID = #{REP_ROUT_ID}
AND WAY_DIV = #{WAY_DIV}

SELECT ROUT_ID, ROUT_NM, REP_ROUT_NM FROM BMS_ROUT_MST WHERE DEL_YN != 'Y'
AND REP_ROUT_ID = #{CONTENT1}
ORDER BY ROUT_NM

SELECT A.ROUT_ID, A.NODE_ID, A.NODE_SN, A.NODE_NM, A.NODE_TYPE, A.LINK_ID, A.STTN_ID, A.STTN_NO, A.CRS_ID, A.ORGA_ID ,A.GPS_X, A.GPS_Y, A.LINK_NODE_YN, B.MORN_STD FROM BMS_ROUT_NODE_CMPSTN A LEFT OUTER JOIN BMS_ROUT_LINK_CMPSTN B ON A.ROUT_ID = B.ROUT_ID AND A.LINK_ID = B.LINK_ID LEFT OUTER JOIN BMS_ROUT_MST C ON A.ROUT_ID = C.ROUT_ID
AND A.NODE_TYPE != 'NT005' AND A.NODE_TYPE = #{NODE_TYPE}
AND A.NODE_TYPE != 'NT006'
AND A.LINK_NODE_YN = 'Y'
AND A.NODE_TYPE != 'NT005'
AND A.ROUT_ID = #{ROUT_ID} AND A.NODE_TYPE != 'NT006'
AND A.NODE_TYPE != 'NT006'
AND C.REP_ROUT_ID = #{CONTENT1}
AND A.ROUT_ID = #{CONTENT2}
ORDER BY ROUT_ID, NODE_SN

SELECT A.ROUT_ID, A.NODE_ID, A.NODE_SN, A.NODE_NM, A.NODE_TYPE, A.STTN_ID, A.STTN_NO, A.CRS_ID, A.GPS_X, A.GPS_Y FROM BRT_MAIN_ROUT_NODE_INFO A
A.ROUT_ID = #{ROUT_ID}
AND A.NODE_TYPE = #{NODE_TYPE}
ORDER BY NODE_SN
