head.script:
	scwin.onpageload = function() {

		scwin.param = com.getParameter();
		scwin.closeCallbackFncName = scwin.param.callbackFn;
		com.setEnterKeyEvent(popup_header, scwin.btn_search_onclick);

		dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.setJSON(scwin.param.data5, true);
		//dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.setJSON(scwin.param.data6, true);

		dma_sub_search.set("ROUT_ID", scwin.param.data);
		dma_sub_search.set("DAY_DIV", scwin.param.data2);
		dma_sub_search.set("ROUT_NM", scwin.param.data3);
		dma_sub_search.set("OPER_SN", scwin.param.data4);


		dma_sub_search.set("OPER_SN", dma_sub_search.get("OPER_SN")+'회차');		

		scwin.DrawChart();		
	};

	scwin.onpageunload = function() {

	};

	scwin.sub_AL0203G0P0_submitdone = function(e) {

	};

	scwin.sub_OPER_ALL_REAL_NODE_submitdone = function(e) {

	};



	function date_ascending(a, b) {
       return a - b;
    };


	var CharData = function(){
		this.seriesname = {};
		this.fill = {};
		this.backgroundColor = {};
		this.borderColor = {};
		this.data = [];
		this.borderWidth = {}; //선 width
		this.pointBorderWidth = {}; //포인터 width
		this.tension = {};	//곡선 or 직선
		this.pointRadius = {};	//포인트 반지름 0일 경우 포인트 안그려짐
	}

	scwin.DrawChart = function(){

		var datasets = new Array();
		var transData = new Array();
		var i = 0;
		var j = 0;
		var num = 0;

		var rowCount = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getRowCount();
		var jsonData = dlt_BRT_CUR_CHG_OPER_DTL_INFO_HIS.getAllJSON();
		//var rlAllJson = dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getAllJSON();

		var data = new Array();
		for(var y = 0; y < jsonData.length; y++){
			var label = jsonData[y].OPER_SN + "회차";
			//var updDtm = jsonData[y].UPD_DTM;
			var updDtm = util.getCurrentDate3();
			var operSn = jsonData[y].OPER_SN;
			var arrvTm = jsonData[y].ARRV_TM;
			var dprtTm = jsonData[y].DPRT_TM;
			var accruLen = jsonData[y].ACCRU_LEN;
			var nodeType = jsonData[y].NODE_TYPE;

			if(nodeType=='NT002'){
				var categoryJson = new Object();

				categoryJson.x = new Date(updDtm+' '+arrvTm);
				categoryJson.y = accruLen;
				data.push(categoryJson);

			}

			if((nodeType=='NT002')&&(arrvTm!=dprtTm)){
				var categoryJson = new Object();

				categoryJson.x = new Date(updDtm+' '+dprtTm);
				categoryJson.y = accruLen;
				data.push(categoryJson);
			}

		};

		var charData = new CharData();

		charData.data = data;
		charData.label = label+" 운행계획";

		charData.backgroundColor = 'rgba(0, 0, 255, 0.5)';
		charData.borderColor = 'rgba(0, 0, 255, 0.5)';
		charData.borderWidth = 1;
		charData.pointBorderWidth = 1;
		charData.tension = 0;
		charData.pointRadius = 1;

		datasets.push(charData);

		var data = new Array();
		for(var y = 0; y < jsonData.length; y++){
			var label = jsonData[y].OPER_SN + "회차";
			//var updDtm = jsonData[y].UPD_DTM;
			var updDtm = util.getCurrentDate3();
			var operSn = jsonData[y].OPER_SN;
			var arrvTm = jsonData[y].CHG_ARRV_TM;
			var dprtTm = jsonData[y].CHG_DPRT_TM;
			var accruLen = jsonData[y].ACCRU_LEN;
			var nodeType = jsonData[y].NODE_TYPE;

			if(nodeType=='NT002'){
				var categoryJson = new Object();

				categoryJson.x = new Date(updDtm+' '+arrvTm);
				categoryJson.y = accruLen;
				data.push(categoryJson);

			}

			if((nodeType=='NT002')&&(arrvTm!=dprtTm)){
				var categoryJson = new Object();

				categoryJson.x = new Date(updDtm+' '+dprtTm);
				categoryJson.y = accruLen;
				data.push(categoryJson);
			}

		};

		var charData = new CharData();

		charData.data = data;
		charData.label = label+" 변경운행";

		charData.backgroundColor = 'rgba(255, 99, 132, 1)';
		charData.borderColor = 'rgba(255, 99, 132, 1)';
		charData.borderWidth = 1;
		charData.pointBorderWidth = 1;
		charData.tension = 0;
		charData.pointRadius = 1;

		datasets.push(charData);


		var ctx = document.getElementById('myChart');
		var myChart = new Chart(ctx, {
			    type: 'line',
				data: {
					datasets
				    /*datasets: [{
					      label: '운행계획',
					      data: [{
					        x: new Date(2021, 10, 19, 03, 24, 20),
					        y: 1
					      }, {
					        x: new Date(2021, 10, 19, 4, 24, 0),
					        y: 10
					      }
					      ]
					    },
					    {
					      label: '실제운행',
					      data: [{
					        x: new Date(2021, 10, 19, 3, 28, 0),
					        y: 2
					      }, {
					        x: new Date(2021, 10, 19, 4, 24, 0),
					        y: 11
					      }]
					    }
					    ]*/
				  },
			    options: {
			        scales: {
			           xAxes: [{
				        type: 'time',
				        time: {
				          unit: 'minute'
				        }
				      }]
			        }

			    }
			});
	}
