head.script:
    //처음 실행할 때 해당 돌발의 노선 차량들과 반경3km 차량들을 추가합니다.  
	scwin.onpageload = function() {
		scwin.param = com.getParameter();

		dma_search.set("REP_ROUT_ID", scwin.param.incdnt_rep_rout_id);
		messageText.setValue(scwin.param.incdnt_detail);

		scwin.closeCallbackFncName = scwin.param.callbackFn;
		com.setEnterKeyEvent(popup_header, scwin.btn_search_onclick);
		var btnOptions = 
		[
			{userStr:"조회", auth: gcm.BTN.SEARCH, cbFnc: scwin.btn_search_onclick}
		];
		com.setMainBtn3(wfm_mainBtn, btnOptions);

		var codeOptions = [{ code : "AREA", compID : "vhcListGrid:AREA"}
						];

		dma_search.set("REP_ROUT_ID", scwin.param.data);

		com.executeSubmission(sub_curOperList);
		com.executeSubmission(sub_sttnList);

		com.setCommonDtl(codeOptions, function() {
			com.searchGrid(vhcMsgList, sub_MO0303P3R1);			
		});

		REP_ROUT_NM.setValue(scwin.param.incdnt_rep_rout_nm);
		VHC_NO.setValue(scwin.param.incdnt_vhc_no);
		ROUT_NM.setValue(scwin.param.incdnt_rout_nm);
		LINK_NM.setValue(scwin.param.incdnt_link_nm);
		INCDNT_ID.setValue(scwin.param.incdnt_id);
		INCDNT_TYPE.setValue(scwin.param.incdnt_type);
		INCDNT_DETAIL.setValue(scwin.param.incdnt_detail);
		INCDNT_RES_YN.setValue(scwin.param.incdnt_res_yn);

		addDataCheckBox.checkAll(true);		

	};

	scwin.onpageunload = function() {

	};

	scwin.btn_search_onclick = function() {
		com.executeSubmission(sub_vhcList);
	};

	scwin.btn_confirm_onclick = function() {
		var jsonData = vhcMsgList.getCheckedJSON("chk");
		var vhcArray = new Array();

		for (i=0; i<jsonData.length; i++) {
			vhcArray[i] = jsonData[i].VHC_ID;	
		}

		var sendMessage = messageText.getValue(); 

		if(sendMessage != "") {
			dma_search.set("VHC_IDS", vhcArray);
			dma_search.set("MSG_CONTS", sendMessage);
			var messageKind = messageType.getValue();

			if(showList.getValue() == "vhcList") {
				if (messageKind == "incdnt") {
					com.executeSubmission(sub_MO0303SCK0);
				}	

				else if (messageKind == "incdntRes") {
					com.executeSubmission(sub_MO0303SCK1);
				}		
			} 

			//정류소일때 메시지 보내기
			else if(showList.getValue() == "sttnList") {

			}

			com.closePopup();
		}		

		else {
			com.alert("돌발 메시지 내용을 입력해 주세요.");	
		}

	};

	scwin.btn_cancel_onclick = function() {
		com.closePopup();
	};

	scwin.sbx_ChoiceBox_onviewchange = function(info) {		

		if (info.newValue == "ALL") {
			acb_autoChoiceBox.hide();
			search_input.setValue("");
			search_input.show("");
		} 
		else if (info.newValue == "VHC_NO") {
			search_input.hide();
			acb_autoChoiceBox.setNodeSet("data:dlt_vhcItem", "VHC_NO", "VHC_NO");
			acb_autoChoiceBox.show("");
		}
		else if (info.newValue == "VHC_ID") {
			search_input.hide();
			acb_autoChoiceBox.setNodeSet("data:dlt_vhcItem", "VHC_ID", "VHC_ID");
			acb_autoChoiceBox.show("");
		}		
	};

	scwin.sub_vhcList_submitdone = function(e) {
		com.excludeItemsByGrid(vhcListGrid, dma_VHC_IDS.get("VHC_IDS"), "VHC_ID");
	};

	scwin.NullData_onviewchange = function(info) {

		if(info.checked == true){
			com.executeSubmission(sub_vhcList);
			vhcListGrid.setDisabled("grid", true);
			vhcListGrid.setDisabled("column","chk",true);
		}
		else{
			vhcListGrid.setDisabled("grid", false);
			vhcListGrid.setDisabled("column","chk",false);
		}

	};

	// -> 버튼
	scwin.btn_grid_add_onclick = function() {
		if(showList.getValue() == "vhcList") {
			var rowIndexArr = vhcListGrid.getCheckedIndex("chk");
			var checkedJson = vhcListGrid.getCheckedJSON("chk");

			for(i=0; i<checkedJson.length; i++) {
				var insertIndex = dlt_VHC_MESSAGE_LIST.insertRow();			
				dlt_VHC_MESSAGE_LIST.setCellData(insertIndex, "VHC_ID", checkedJson[i].VHC_ID);
				dlt_VHC_MESSAGE_LIST.setCellData(insertIndex, "VHC_NO", checkedJson[i].VHC_NO);					
			}
			dlt_BMS_VHC_MST.removeRows(rowIndexArr);
		}
	};

	// <- 버튼
	scwin.btn_grid_del_onclick = function() {

		if(showList.getValue() == "vhcList") {
			var rowIndexArr = vhcMsgList.getCheckedIndex("chk");
			var checkedJson = vhcMsgList.getCheckedJSON("chk");

			for(i=0; i<checkedJson.length; i++) {
				var insertIndex = dlt_BMS_VHC_MST.insertRow();			
				dlt_BMS_VHC_MST.setCellData(insertIndex, "VHC_ID", checkedJson[i].VHC_ID);
				dlt_BMS_VHC_MST.setCellData(insertIndex, "VHC_NO", checkedJson[i].VHC_NO);					
			}
			dlt_VHC_MESSAGE_LIST.removeRows(rowIndexArr);

			dlt_BMS_VHC_MST.sort("VHC_ID", 0);

		}
	};	

	scwin.sub_MO0303P3R1_submitdone = function(e) {
		var incdnt_gps_x = scwin.param.incdnt_gps_x;
		var incdnt_gps_y = scwin.param.incdnt_gps_y;
		var circleLatLng = new kakao.maps.LatLng(scwin.param.incdnt_gps_y, scwin.param.incdnt_gps_x);
		var incdnt_rep_rout_id = scwin.param.incdnt_rep_rout_id;

		var circle = new kakao.maps.Circle({
		    center : circleLatLng,  // 원의 중심좌표 입니다 
		    radius: 3000 // 미터 단위의 원의 반지름입니다 
		}); 		

		for (i=0; i<dlt_VHC_MESSAGE_LIST.getDataLength(); i++){
			var car_gps_x = dlt_VHC_MESSAGE_LIST.getCellData(i, "GPS_X");
			var car_gps_y = dlt_VHC_MESSAGE_LIST.getCellData(i, "GPS_Y");
			var carLatLng = new kakao.maps.LatLng(car_gps_y, car_gps_x);

			//돌발 위치와 차 위치를 경로로 하는 폴리라인 설정
			var line = new kakao.maps.Polyline();
			var path = [carLatLng, circleLatLng];
			line.setPath(path);

			//차 좌표와 돌발 중심 사이의 거리
			var dist = line.getLength();

			//이 거리가 원의 반지름보다 크다면 차는 돌발 알림 범위밖에 있음
			if (dist > circle.getRadius()) {
				dlt_VHC_MESSAGE_LIST.removeRow(i);
				i = i-1;
			}

			//현재 운행 차량중 해당노선이 아님
			if(dlt_VHC_MESSAGE_LIST.getCellData(i, "REP_ROUT_ID") != incdnt_rep_rout_id) {
				dlt_VHC_MESSAGE_LIST.removeRow(i);	
				i = i-1;
			}		

		}

		vhcMsgList.checkAll("chk", 1);

		//com.searchGrid(vhcListGrid, sub_vhcList);
		com.executeSubmission(sub_allocVhcList);

	};

	scwin.sub_allocVhcList_submitdone = function(e) {
		var incdnt_gps_x = scwin.param.incdnt_gps_x;
		var incdnt_gps_y = scwin.param.incdnt_gps_y;
		var incdnt_rep_rout_id = scwin.param.incdnt_rep_rout_id;

		for(i=0; i<dlt_BRT_DAY_ALLOC_PL_INFO.getDataLength(); i++) {
			if(dlt_BRT_DAY_ALLOC_PL_INFO.getCellData(i, "REP_ROUT_ID") == incdnt_rep_rout_id) {
				var allocVhcId = dlt_BRT_DAY_ALLOC_PL_INFO.getCellData(i, "VHC_ID");
				var duplCheck = dlt_VHC_MESSAGE_LIST.getMatchedIndex("VHC_ID", allocVhcId);

				//메시지 전송 차량 목록에 없을때만 새로 추가			
				if(duplCheck.length == 0 ) {
					var insertIndex = dlt_VHC_MESSAGE_LIST.insertRow();
					dlt_VHC_MESSAGE_LIST.setCellData(insertIndex, "REP_ROUT_ID", incdnt_rep_rout_id);
					dlt_VHC_MESSAGE_LIST.setCellData(insertIndex, "VHC_ID", dlt_BRT_DAY_ALLOC_PL_INFO.getCellData(i, "VHC_ID"));
					dlt_VHC_MESSAGE_LIST.setCellData(insertIndex, "VHC_NO", dlt_BRT_DAY_ALLOC_PL_INFO.getCellData(i, "VHC_NO"));
					dlt_VHC_MESSAGE_LIST.setCellData(insertIndex, "COMP_NM", dlt_BRT_DAY_ALLOC_PL_INFO.getCellData(i, "COMP_NM"));
				}
			}
		}

		/*
		//컬럼 dstinct된 데이터들
		var distinct_VHC_IDS = dlt_VHC_MESSAGE_LIST.getColumnDistinctDataArray("VHC_ID");
		var distinct_VHC_NOS = dlt_VHC_MESSAGE_LIST.getColumnDistinctDataArray("VHC_NO");

		dlt_VHC_MESSAGE_LIST.removeAll();

		for (i=0; i<distinct_VHC_IDS.length; i++) {
			var insertIndex = dlt_VHC_MESSAGE_LIST.insertRow();			
			dlt_VHC_MESSAGE_LIST.setCellData(insertIndex, "VHC_ID", distinct_VHC_IDS[i]);
			dlt_VHC_MESSAGE_LIST.setCellData(insertIndex, "VHC_NO", distinct_VHC_NOS[i]);			
		}
		*/

		dlt_VHC_MESSAGE_LIST.sort("VHC_ID", 0);

		vhcMsgList.checkAll("chk", 1);

		var val_VHC_IDS = dlt_VHC_MESSAGE_LIST.getColData("VHC_ID");
		dma_VHC_IDS.set("VHC_IDS", val_VHC_IDS);

		//com.excludeItemsByGrid(vhcMsgList, dma_VHC_IDS.get("VHC_IDS"), "VHC_ID");

		//var val_GRG_ID = dlt_BMS_GRG_MST.getColData("GRG_ID");
		//com.excludeItemsByGrid(SI0102P0, ids, "GRG_ID");
		//dma_param_COMPID.set("GRG_IDS", val_GRG_ID);
		com.searchGrid(vhcListGrid, sub_vhcList);
	};

	scwin.vhcListGrid_oncellclick = function(row,col,colId) {
		if(col==0)return;
		if(vhcListGrid.getCellChecked(row,"chk")){
			vhcListGrid.setCellChecked(row,"chk",false)
		}
		else {
			vhcListGrid.setCellChecked(row,"chk",true)
		}		
	};

	scwin.sttnListGrid_oncellclick = function(row,col,colId) {
		if(col==0)return;
		if(sttnListGrid.getCellChecked(row,"chk")){
			sttnListGrid.setCellChecked(row,"chk",false)
		}
		else {
			sttnListGrid.setCellChecked(row,"chk",true)
		}		
	}

	scwin.vhcMsgList_oncellclick = function(row,col,colId) {
		if(col==0)return;
		if(vhcMsgList.getCellChecked(row,"chk")){
			vhcMsgList.setCellChecked(row,"chk",false)
		}
		else {
			vhcMsgList.setCellChecked(row,"chk",true)
		}			
	};

	scwin.sttnMsgList_oncellclick = function(row,col,colId) {
		if(col==0)return;
		if(sttnMsgList.getCellChecked(row,"chk")){
			sttnMsgList.setCellChecked(row,"chk",false)
		}
		else {
			sttnMsgList.setCellChecked(row,"chk",true)
		}			
	};

	scwin.addDataCheckBox_onviewchange = function(info) {
		selectedValue = info.value;
		checkYN = info.checked;

			if(selectedValue == "route") {
				if(checkYN) {
					for (i=0; i<dlt_BRT_DAY_ALLOC_PL_INFO.getDataLength(); i++){
						var repRoutId = dlt_BRT_DAY_ALLOC_PL_INFO.getCellData(i, "REP_ROUT_ID");
						var vhcId = dlt_BRT_DAY_ALLOC_PL_INFO.getCellData(i, "VHC_ID");

						if (scwin.param.incdnt_rep_rout_id == repRoutId) {
							var duplCheck = dlt_VHC_MESSAGE_LIST.getMatchedIndex("VHC_ID", vhcId);
							if (duplCheck.length == 0) {
								var rowIndexArr = dlt_BMS_VHC_MST.getMatchedIndex("VHC_ID", vhcId);
								dlt_BMS_VHC_MST.removeRow(rowIndexArr[0]);
								var insertRowIndex = dlt_VHC_MESSAGE_LIST.insertRow();
								dlt_VHC_MESSAGE_LIST.setCellData(insertRowIndex, "chk", 1);
								dlt_VHC_MESSAGE_LIST.setCellData(insertRowIndex, "VHC_ID", dlt_BRT_DAY_ALLOC_PL_INFO.getCellData(i, "VHC_ID"));
								dlt_VHC_MESSAGE_LIST.setCellData(insertRowIndex, "VHC_NO", dlt_BRT_DAY_ALLOC_PL_INFO.getCellData(i, "VHC_NO"));
								dlt_VHC_MESSAGE_LIST.setCellData(insertRowIndex, "REP_ROUT_ID", dlt_BRT_DAY_ALLOC_PL_INFO.getCellData(i, "REP_ROUT_ID"));

								dlt_VHC_MESSAGE_LIST.sort("VHC_ID", 0);
							}
						}
					} //end for

				}

				else if(!checkYN) {
					for (i=0; i<dlt_VHC_MESSAGE_LIST.getDataLength(); i++){
						var repRoutId = dlt_VHC_MESSAGE_LIST.getCellData(i, "REP_ROUT_ID");
						if (scwin.param.incdnt_rep_rout_id == repRoutId) {
							var insertRowIndex = dlt_BMS_VHC_MST.insertRow();
							dlt_BMS_VHC_MST.setCellData(insertRowIndex, "VHC_ID", dlt_VHC_MESSAGE_LIST.getCellData(i, "VHC_ID"));
							dlt_BMS_VHC_MST.setCellData(insertRowIndex, "VHC_NO", dlt_VHC_MESSAGE_LIST.getCellData(i, "VHC_NO"));
							var removeRowIndex = dlt_VHC_MESSAGE_LIST.removeRow(i);

							dlt_BMS_VHC_MST.sort("VHC_ID", 0);

							//하나라도 지워지면 모두 체크해제하여 리스트 데이터 신뢰성 유지
							addDataCheckBox.checkAll(false);

							i = i-1;
						}
					}							
				}			
		}

		else if(selectedValue == "section") {

			var circleLatLng = new kakao.maps.LatLng(scwin.param.incdnt_gps_y, scwin.param.incdnt_gps_x);

			var circle = new kakao.maps.Circle({
			    center : circleLatLng,  // 원의 중심좌표 입니다 
			    radius: 3000 // 미터 단위의 원의 반지름입니다 
			}); 

			if(checkYN) {
				//dlt_BRT_CUR_OPER_INFO는 위치값을 가져오기 위한 고정 List, List 개수 변하지 않음
				for (i=0; i<dlt_BRT_CUR_OPER_INFO.getDataLength(); i++){
					var curOperVhcId = dlt_BRT_CUR_OPER_INFO.getCellData(i, "VHC_ID");
					var curOperVhcNo = dlt_BRT_CUR_OPER_INFO.getCellData(i, "VHC_NO");
					var car_gps_x = dlt_BRT_CUR_OPER_INFO.getCellData(i, "GPS_X");
					var car_gps_y = dlt_BRT_CUR_OPER_INFO.getCellData(i, "GPS_Y");
					var carLatLng = new kakao.maps.LatLng(car_gps_y, car_gps_x);

					//돌발 위치와 차 위치를 경로로 하는 폴리라인 설정
					var line = new kakao.maps.Polyline();
					var path = [carLatLng, circleLatLng];
					line.setPath(path);

					//차 좌표와 돌발 중심 사이의 거리
					var dist = line.getLength();

					//이 거리가 원의 반지름보다 작다면 차는 돌발 알림 범위안에 있음
					if (dist < circle.getRadius()) {
						var duplCheck = dlt_VHC_MESSAGE_LIST.getMatchedIndex("VHC_ID", curOperVhcId);
						if(duplCheck.length == 0) {
							var rowIndexArr = dlt_BMS_VHC_MST.getMatchedIndex("VHC_ID", curOperVhcId);
							dlt_BMS_VHC_MST.removeRow(rowIndexArr[0]);							
							var insertRowIndex = dlt_VHC_MESSAGE_LIST.insertRow();
							dlt_VHC_MESSAGE_LIST.setCellData(insertRowIndex, "chk", 1);
							dlt_VHC_MESSAGE_LIST.setCellData(insertRowIndex, "VHC_ID", curOperVhcId);
							dlt_VHC_MESSAGE_LIST.setCellData(insertRowIndex, "VHC_NO", curOperVhcNo);
							dlt_VHC_MESSAGE_LIST.setCellData(insertRowIndex, "GPS_X", car_gps_x);
							dlt_VHC_MESSAGE_LIST.setCellData(insertRowIndex, "GPS_Y", car_gps_y);
							dlt_VHC_MESSAGE_LIST.sort("VHC_ID", 0);
						}
					}
				}
			}

			else if (!checkYN) {

				for (i=0; i<dlt_VHC_MESSAGE_LIST.getDataLength(); i++){
					var car_gps_x = dlt_VHC_MESSAGE_LIST.getCellData(i, "GPS_X");
					var car_gps_y = dlt_VHC_MESSAGE_LIST.getCellData(i, "GPS_Y");
					var curOperVhcId = dlt_VHC_MESSAGE_LIST.getCellData(i, "VHC_ID");
					var curOperVhcNo = dlt_VHC_MESSAGE_LIST.getCellData(i, "VHC_NO");									
					var carLatLng = new kakao.maps.LatLng(car_gps_y, car_gps_x);

					if (car_gps_x != "" && car_gps_y != "") {
						//돌발 위치와 차 위치를 경로로 하는 폴리라인 설정
						var line = new kakao.maps.Polyline();
						var path = [carLatLng, circleLatLng];
						line.setPath(path);				

						//차 좌표와 돌발 중심 사이의 거리
						var dist = line.getLength();						

						//이 거리가 원의 반지름보다 작다면 차는 돌발 알림 범위안에 있음
						if (dist < circle.getRadius()) {

							var insertRowIndex = dlt_BMS_VHC_MST.insertRow();
							dlt_BMS_VHC_MST.setCellData(insertRowIndex, "VHC_ID", dlt_VHC_MESSAGE_LIST.getCellData(i, "VHC_ID"));
							dlt_BMS_VHC_MST.setCellData(insertRowIndex, "VHC_NO", dlt_VHC_MESSAGE_LIST.getCellData(i, "VHC_NO"));
							var removeRowIndex = dlt_VHC_MESSAGE_LIST.removeRow(i);

							dlt_BMS_VHC_MST.sort("VHC_ID", 0);

							//하나라도 지워지면 모두 체크해제하여 리스트 데이터 신뢰성 유지
							addDataCheckBox.checkAll(false);

							i = i-1;							
						}
					}
				}				
			}

		} //end else if(section)

	};

	scwin.showList_onviewchange = function(info) {
		selectedValue = info.value;

		if(info.value == "vhcList") {
			sttnListGrid.hide();
			sttnMsgList.hide();
			sttnMsgList.checkAll("chk", 0);
			vhcListGrid.show();	
			vhcMsgList.show();		
		}

		else if(info.value == "sttnList") {
			sttnListGrid.show();
			sttnMsgList.show();
			vhcMsgList.checkAll("chk", 0);
			vhcListGrid.hide();
			vhcMsgList.hide();
		}

	};
