head.script:
	var ledFocus = 0;
	var soundFocus = 0;
	var etcFocus = 0;	
	var ledActive = 1;
	var soundActive = 0;
	var etcActive = 0;	

	//해야할 남은것들!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
	//지점음성 레이아웃 뭉개지는거 고쳐야함
	//업로드 이벤트같은 로직 추가안했음
	scwin.onpageload = function() {
		var autoOptions = {
			Main : { grid : PI0801G0, frm : PI0801F0, srchSbm : sub_PI0801G0R0, savSbm : sub_PI0801G0S0, keySbm : sub_PI0801G0K0,
					srchGrp : pgm_header, keyColumn : "ILD_ID", focusColumn : "ILD_NM", name : "기본문구 목록"}
		 };		

		var mainOptions = {
			ADD : scwin.add_onclick,
			SAVE : scwin.save_onclick	
		};

      	var subOptions = {
         SAVE:[{cbFnc:scwin.btn_up_onclick, nm:"상",class:"up"},{cbFnc:scwin.btn_down_onclick,nm:"하",class:"down"}]

         };

		com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.SINGLE_GRID_FORM, autoOptions, mainOptions);	
      	com.setSubBtn2(wfm_subBtn, autoOptions, subOptions);		

		com.searchGrid(PI0801G0, sub_PI0801G0R0);
		com.searchGrid(PI0801G1, sub_PI0801G1R0);
		com.searchGrid(PI0801G2, sub_PI0801G2R0);

		com.executeSubmission(sub_PI0801G1R0);		
		com.executeSubmission(sub_PI0801SHI0);

		//탭 목록 클릭 이벤트
		$(".moni_img").find("li").click( function() {
			var li_id = this.id;
			$(".moni_img").find("li").removeClass("on");
			$(".moni_img").find("#"+li_id).addClass("on");
			if(li_id == "li_ledimg") {
				search_input.setPlaceholder("LED ID 또는 LED명");
				ledActive = 1;
				soundActive = 0;
				etcActive = 0;

				$(".PI0801T0").css("display", "block");			
				$(".PI0801T0").css("visibility", "visible");			
				$(".PI0801T1").css("display", "none");			
				$(".PI0801T1").css("visibility", "hidden");			
				$(".PI0801T2").css("display", "none");			
				$(".PI0801T2").css("visibility", "hidden");			


			//	PI0801T0.show();
			//	PI0801T1.hide();
			//	PI0801T2.hide();
				if(ledFocus != 0){
					PI0801G0.setFocusedCell(ledFocus, "ILD_ID");
				} else if (ledFocus == 0) {
					com.setFocusedCell(PI0801G0, "ILD_ID");	
				}
				scwin.PI0801G0_onrowindexchange();			
			}
			else if(li_id == "li_soundimg") {
				search_input.setPlaceholder("음성ID 또는 음성명")
				ledActive = 0;
				soundActive = 1;
				etcActive = 0;

				$(".PI0801T0").css("display", "none");			
				$(".PI0801T0").css("visibility", "hidden");			
				$(".PI0801T1").css("display", "block");			
				$(".PI0801T1").css("visibility", "visible");			
				$(".PI0801T2").css("display", "none");			
				$(".PI0801T2").css("visibility", "hidden");					

			//	PI0801T0.hide();
			//	PI0801T1.show();
			//	PI0801T2.hide();
				if(soundFocus != 0){
					PI0801G1.setFocusedCell(soundFocus, "VOC_ID");
				} else if (soundFocus == 0) {
					com.setFocusedCell(PI0801G1, "VOC_ID");	
				}				
				scwin.PI0801G1_onrowindexchange();	
			}
			else if(li_id == "li_etcimg") {
				search_input.setPlaceholder("음성ID 또는 음성명")
				ledActive = 0;
				soundActive = 0;
				etcActive = 1;

				$(".PI0801T0").css("display", "none");			
				$(".PI0801T0").css("visibility", "hidden");			
				$(".PI0801T1").css("display", "none");			
				$(".PI0801T1").css("visibility", "hidden");			
				$(".PI0801T2").css("display", "block");			
				$(".PI0801T2").css("visibility", "visible");		

			//	PI0801T0.hide();
			//	PI0801T1.hide();
			//	PI0801T2.show();
				if(etcFocus != 0){
					PI0801G2.setFocusedCell(etcFocus, "VOC_ID");
				} else if (etcFocus == 0) {
					com.setFocusedCell(PI0801G2, "VOC_ID");	
				}				
				scwin.PI0801G2_onrowindexchange();	
			}
		});		

	};

	scwin.onpageunload = function() {

	};

	scwin.sbx_ChoiceBox_onviewchange = function(info) {

		if(ledActive) {
			if (info.newValue == "ALL") {
				autoChoiceBox.hide();
				search_input.setValue("");
				search_input.show("");
			} 
			else if (info.newValue == "ILD_ID") {
				search_input.hide();
				autoChoiceBox.setNodeSet("data:dlt_searchitem", "ILD_ID", "ILD_ID");
				autoChoiceBox.show("");
			}
			else if (info.newValue == "ILD_NM") {
				search_input.hide();
				autoChoiceBox.setNodeSet("data:dlt_searchitem", "ILD_NM", "ILD_NM");
				autoChoiceBox.show("");
			} 		
		}

		if(soundActive) {
			if (info.newValue == "ALL") {
				acb_autoChoiceBox.hide();
				search_input.setValue("");
				search_input.show("");
			} else if (info.newValue == "VOC_ID") {
				search_input.hide();
				acb_autoChoiceBox.setNodeSet("data:dlt_searchitem", "VOC_ID", "VOC_ID");
				acb_autoChoiceBox.show("");
			} else if (info.newValue == "VOC_NM") {
				search_input.hide();
				acb_autoChoiceBox.setNodeSet("data:dlt_searchitem", "VOC_NM", "VOC_NM");
				acb_autoChoiceBox.show("");
			}			
		}

		if(etcActive) {
			if (info.newValue == "ALL") {
				autoChoiceBox.hide();
				search_input.setValue("");
				search_input.show("");
			} else if (info.newValue == "VOC_ID") {
				search_input.hide();
				autoChoiceBox.setNodeSet("data:dlt_searchitem", "VOC_ID", "VOC_ID");
				autoChoiceBox.show("");
			} else if (info.newValue == "VOC_NM") {
				search_input.hide();
				autoChoiceBox.setNodeSet("data:dlt_searchitem", "VOC_NM", "VOC_NM");
				autoChoiceBox.show("");
			}		
		}


	};

	scwin.sub_PI0801G0R0_submitdone = function(e) {
