		com.setserialNumberToData(PI0801G0, "SN");
	};

	scwin.btn_up_onclick = function() {
		//상하 이벤트 클릭부터 해야됨
	    var idx = PI0801G0.getFocusedRowIndex();
	    com.getGridViewDataList(PI0801G0).rowMoveUp(idx);
	    var data = dlt_BMS_INNER_LED_INFO.getAllJSON();
	    var count = data.length;

	    for(i=0; i<count; i++) {
		    dlt_BMS_INNER_LED_INFO.setCellData(i, "SN", i+1);	
		   } 

	}

	scwin.btn_down_onclick = function () {
  	    var idx = PI0801G0.getFocusedRowIndex();
   		com.getGridViewDataList(PI0801G0).rowMoveDown(idx);	
	    var data = dlt_BMS_INNER_LED_INFO.getAllJSON();
	    var count = data.length;

	    for(i=0; i<count; i++) {
		    dlt_BMS_INNER_LED_INFO.setCellData(i, "SN", i+1);	
		   }   	
	}

	scwin.save_onclick = function () {

		if(ledActive){
		      var idx = PI0801G0.getModifiedIndex().length;

		      if (idx == 0) {
		         if((typeof str == "undefined") || (str.trim() == ""))
		            str = "저장할 데이터가 없습니다";
		            com.alert(str);
		         return false;
		      }

		      if (com.validateGridView(PI0801G0)) {
		         if (idx > 0) {
		            var yesno_str = com.strModifiedCnt(PI0801G0) + "의 데이터가 저장됩니다. \n 진행하시겠습니까?";
		            com.confirm(yesno_str, function(rtn) {
		               if (rtn) {          
			              com.setSerialNumberToData(PI0801G0, "SN"); 
		                  com.saveData(PI0801G0,null,sub_PI0801G0S0);
		               }
		            });
		         }
		      }		
		}	

		if(soundActive){
		      var idx = PI0801G1.getModifiedIndex().length;

		      if (idx == 0) {
		         if((typeof str == "undefined") || (str.trim() == ""))
		            str = "저장할 데이터가 없습니다";
		            com.alert(str);
		         return false;
		      }

		      if (com.validateGridView(PI0801G1)) {
		         if (idx > 0) {
		            var yesno_str = com.strModifiedCnt(PI0801G1) + "의 데이터가 저장됩니다. \n 진행하시겠습니까?";
		            com.confirm(yesno_str, function(rtn) {
		               if (rtn) {          
			              //com.setSerialNumberToData(PI0801G1, "SN"); 
		                  com.saveData(PI0801G1,null,sub_PI0801G0S1);
		               }
		            });
		         }
		      }		
		}

		if(etcActive){
		      var idx = PI0801G2.getModifiedIndex().length;

		      if (idx == 0) {
		         if((typeof str == "undefined") || (str.trim() == ""))
		            str = "저장할 데이터가 없습니다";
		            com.alert(str);
		         return false;
		      }

		      if (com.validateGridView(PI0801G1)) {
		         if (idx > 0) {
		            var yesno_str = com.strModifiedCnt(PI0801G2) + "의 데이터가 저장됩니다. \n 진행하시겠습니까?";
		            com.confirm(yesno_str, function(rtn) {
		               if (rtn) {          
			              //com.setSerialNumberToData(PI0801G1, "SN"); 
		                  com.saveData(PI0801G2,null,sub_PI0801G0S2);
		               }
		            });
		         }
		      }		
		}			

	}

	scwin.sub_PI0801G1R0_submitdone = function(e) {
		var rsCode = com.getResultCode(e)
		if (rsCode == gcm.MESSAGE_CODE.STATUS_SUCCESS) {
			com.setFocusedCell(PI0801G1, "VOC_NM");
		}		
	};

	scwin.PI0801G0_onrowindexchange = function(row,oldRow) {
		ledFocus = row;
	};	

	scwin.PI0801G1_onrowindexchange = function(row, oldRow) {
		soundFocus = row;
		var audioType = dlt_BMS_VOC_INFO.getCellData(PI0801G1.getFocusedRowIndex(), "PLAY_TYPE");
		if (audioType == "WAV"){
			//VOC_PATH.setDisabled(false);
			KR_TTS.setDisabled(true);
			ER_TTS.setDisabled(true);
			playTTS.setDisabled(true);
			btn_basicPhrase.setDisabled(true);
			playTTS2.setDisabled(true);
			btn_basicPhrase2.setDisabled(true);							
		} else if (audioType == "TTS"){
			//VOC_PATH.setDisabled(true);
			KR_TTS.setDisabled(false);
			ER_TTS.setDisabled(false);
			playTTS.setDisabled(false);
			btn_basicPhrase.setDisabled(false);
			playTTS2.setDisabled(false);
			btn_basicPhrase2.setDisabled(false);							
		}
		var path = dlt_BMS_VOC_INFO.getCellData(PI0801G1.getFocusedRowIndex(), "VOC_PATH");	
		$p.$("#audio0").attr("src", path);			

	};		

	scwin.PI0801G2_onrowindexchange = function(row, oldRow) {
		etcFocus = row;
		var audioType = dlt_BMS_VOC_INFO.getCellData(PI0801G2.getFocusedRowIndex(), "PLAY_TYPE");

		//오디오 타입에따라 입력창 활성, 비활성화
		if (audioType == "WAV"){
			VOC_PATH2.setDisabled(false);
			KR_TTS2.setDisabled(true);
			EN_TTS2.setDisabled(true);
			playTTS3.setDisabled(true);
			btn_basicPhrase3.setDisabled(true);
			playTTS4.setDisabled(true);
			btn_basicPhrase4.setDisabled(true);			
		} else if (audioType == "TTS"){
//			VOC_PATH2.setDisabled(true);
			KR_TTS2.setDisabled(false);
			EN_TTS2.setDisabled(false);
			playTTS3.setDisabled(false);
			btn_basicPhrase3.setDisabled(false);
			playTTS4.setDisabled(false);
			btn_basicPhrase4.setDisabled(false);						
		}	

		//오디오 세팅
		var path = dlt_BMS_VOC_INFO.getCellData(PI0801G2.getFocusedRowIndex(), "VOC_PATH");		
		$p.$("#audio1").attr("src", path);	
	};		

	scwin.sub_PI0801G2R0_submitdone = function(e) {
		com.setFocusedCell(PI0801G2, "VOC_NM");
	};	

	scwin.add_onclick = function(e) {

		if(ledActive) {
			com.executeSubmission(sub_PI0801G0K0);	
		}
		if(soundActive) {
			com.executeSubmission(sub_PI0801G1K0);
		}
		if(etcActive) {
			com.executeSubmission(sub_PI0801G2K0);
		}
	};

	scwin.upload0_onInputValueChange = function(fileName) {
		// 확장자 추출
		var lastDot = fileName.lastIndexOf('.'); 
		var fileExtension = fileName.substring(lastDot+1, fileName.length);

		var fileNameIndex = fileName.lastIndexOf('\\');
		//파일명


		if (fileExtension == "wav" || fileExtension == "tts"){
			var real_fileName = fileName.substring(fileNameIndex+1, fileName.length);

			dma_AUDIO_INFO.set('AUDIO_NM', real_fileName);
			dma_AUDIO_INFO.set('AUDIO_EXTENSION', fileExtension);

			dlt_BMS_VOC_INFO.setCellData(PI0801G1.getFocusedRowIndex(), 'VOC_PATH', com.getUploadPath()+dma_AUDIO_INFO.get('AUDIO_NM'));
			VOC_PATH.submit();

        }else{
			com.alert("wav, tts 확장자만 업로드 할 수 있습니다.");
			VOC_PATH.reset()
		}

	};	

	scwin.upload0_ondone = function(retXml) {
		var time_audio = document.getElementById("audio0");

		var path = dlt_BMS_VOC_INFO.getCellData(PI0801G1.getFocusedRowIndex(), "VOC_PATH");
		$p.$("#audio0").attr("src", path);

		time_audio.addEventListener('loadedmetadata', function() {
			var duration = parseInt(time_audio.duration);
			dlt_BMS_VOC_INFO.setCellData(PI0801G1.getFocusedRowIndex(), "PLAY_TM", duration);
		});				

		/*
		 서버에 저장되도 404 error 발생함 바로 갱신이 안되는 문제
		 현재 동일한 파일명으로 업로드 시 (날짜) 형태로 서버에 파일이 생성되는데 괄호가 보안처리에 의해 다른 문자로 치환되서 DB에서 경로 가져올때 문제 있음  */
	};	

