	scwin.insertPhraseEng2 = function(obj) {
		if(obj != null) {
			EN_TTS2.setValue(obj[0].DL_CD_NM);	
		}
	};	

	scwin.VOC_PATH2_ondone = function(retXml) {
		var time_audio = document.getElementById("audio1");

		var path = dlt_BMS_VOC_INFO2.getCellData(PI0801G2.getFocusedRowIndex(), "VOC_PATH");
		$p.$("#audio1").attr("src", path);

		time_audio.addEventListener('loadedmetadata', function() {
			var duration = parseInt(time_audio.duration);
			dlt_BMS_VOC_INFO2.setCellData(PI0801G2.getFocusedRowIndex(), "PLAY_TM", duration);
		});				

	};	

	scwin.PLAY_ST_DT2_onviewchange = function(info) {

		var playstdt = PLAY_ST_DT2.getValue();
		var playeddt = PLAY_ED_DT2.getValue();

		if (playstdt > playeddt && playeddt != ""){
			com.messagBox("alert", "지정할 수 없는 날짜입니다.", scwin.checkCloseBUY(info));
		}
	};	

	scwin.PLAY_ED_DT2_onviewchange = function(info) {
		var playstdt = PLAY_ST_DT.getValue();
		var playeddt = PLAY_ED_DT.getValue();

		if (playstdt > playeddt){
			com.messagBox("alert", "지정할 수 없는 날짜입니다.", scwin.checkCloseINST(info));
		}

		playstdt = playstdt.substring(0, 4) + "-" + playstdt.substring(4, 6) + "-" + playstdt.substring(6)
		playeddt = playeddt.substring(0, 4) + "-" + playeddt.substring(4, 6) + "-" + playeddt.substring(6)

		dlt_BMS_VOC_INFO2.setCellData(PI0801G2.getFocusedRowIndex(), 'PLAY_DATE', playstdt + ' ~ ' + playeddt);		
	};	

	scwin.checkCloseBUY = function(info) {
		PLAY_ST_DT2.setValue(info.oldValue);
	}
	scwin.checkCloseINST = function(info) {
		PLAY_ED_DT2.setValue(info.oldValue);
	}
