var curRoutId;

scwin.onpageload = function() {

	var codeOptions = [{code : "NODE_TYPE", compID : "SI0402G1:NODE_TYPE"},{code : "WAY_DIV", compID : "SI0402G0:WAY_DIV"}];
	com.setCommonDtl(codeOptions, function() {
		com.searchGrid(SI0402G0, sub_SI0402G0R0);
	});	

	com.executeSubmission(sub_SI0402SHI0);

	var autoOptions = {
		Main : { grid : SI0402G0, srchSbm : sub_SI0402G0R0, srchGrp : pgm_header, keyColumn : "ROUT_ID", focusColumn : "ROUT_ID", name : "노선 목록"},
		Sub1 : { grid : SI0402G1, srchSbm : sub_SI0402G1R0, srchGrp : pgm_subList, savSbm : sub_SI0402G1S0, name :  "노선 경로정보"}
	 };

	var mainOptions = {
		ADD:{disp:"hide"},
		DEL:{disp:"hide"},
		CNL:scwin.btn_cancel_onclick,
		SAVE:scwin.btn_save_onclick
	};

	com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.DUAL_GRID2, autoOptions, mainOptions);

	var subOptions = {
		SAVE:[{cbFnc:scwin.subGridUp, nm:"상",class:"up"},{cbFnc:scwin.subGridDown,nm:"하",class:"down"}]
		, DEL:scwin.btn_sub_del
		, SEARCH: scwin.btn_sub_move
	};

	com.setSubBtn2(wfm_subBtn, autoOptions, subOptions);

	var subOptions2 = {
		SAVE:[{cbFnc:scwin.addPathClick,nm:"경로추가",class:"addPath"},{cbFnc:scwin.addVertexClick,nm:"버텍스추가",class:"addVertex"},{cbFnc:scwin.addSttnClick,nm:"정류소추가",class:"addSttn"}
				,{cbFnc:scwin.addCrossClick,nm:"교차로추가",class:"addCross"},{cbFnc:scwin.btn_sub_del,nm:"선택삭제",class:"del"}
		]
	};

	com.setSubBtn2(wfm_subBtn2, autoOptions, subOptions2);

	SI0402G0.mergeCells("byrestriccol");


	routMap.initMap("map_SI0402", {width:"100%"
		, height:"100%"
		, onClick: scwin.onClickMap
		, nodeChangeCb: scwin.nodeChangeCb
		, draggable: true
	});


	scwin.disp_check_normal();
};


scwin.onpageunload = function() {

};

scwin.sub_SI0402G0R0_submitdone = function(e) {
	scwin.releaseGeoBtn();
	com.setFocusedCell(SI0402G0, "ROUT_ID");	
};

scwin.sbx_ChoiceBox_onviewchange = function(info) {
	if (info.newValue == "ALL") {
		autoChoiceBox.hide();
		search_input.setValue("");
		search_input.show("");
	} 
	else if (info.newValue == "ROUT_ID") {
		search_input.hide();
		autoChoiceBox.setNodeSet("data:dlt_routItem", "ROUT_ID", "ROUT_ID");
		autoChoiceBox.show("");
	}
	else if (info.newValue == "ROUT_NM") {
		search_input.hide();
		autoChoiceBox.setNodeSet("data:dlt_routItem", "ROUT_NM", "ROUT_NM");
		autoChoiceBox.show("");
	} else if (info.newValue == "REP_ROUT_NM") {
		search_input.hide();
		autoChoiceBox.setNodeSet("data:dlt_routItem", "REP_ROUT_NM", "REP_ROUT_NM");
		autoChoiceBox.show("");
	}		
};

scwin.SI0402G0_onrowindexchange = function(row,oldRow) {

	routMap.initMapInfo("map_SI0402");
	curRoutId = dlt_BMS_ROUT_MST.getCellData(row,"ROUT_ID");
	dma_sub_search.set("ROUT_ID", dlt_BMS_ROUT_MST.getCellData(row,"ROUT_ID"));
	dma_sub_search.set("WAY_DIV", dlt_BMS_ROUT_MST.getCellData(row,"WAY_DIV"));
	var focusOption = {keyMap : dma_sub_search};
	com.changeDualGrid(SI0402G0, SI0402G1, sub_SI0402G1S0, sub_SI0402G1R0, focusOption, row, oldRow);
};

scwin.SI0402G1_onviewchange = function(info) {

	if(info.colIndex == 2){
		oldNodeType = info.oldValue;

		if (info.newValue == "NT005") {
			com.alert("버텍스로 변경 할수 없습니다.");
			dlt_BMS_ROUT_NODE_CMPSTN.setCellData(SI0402G1.getFocusedRowIndex(),"NODE_TYPE", oldNodeType);
			return;
		}

		if (info.newValue == "NT006") {
			com.alert("음성노드는 음성 편성에서만 선택할 수 있습니다.");
			dlt_BMS_ROUT_NODE_CMPSTN.setCellData(SI0402G1.getFocusedRowIndex(),"NODE_TYPE", oldNodeType);
			return;
		}

		if ((info.newValue == "NT001") || (info.newValue == "NT002")) { //교차로, 정류소 선택
			if(info.newValue == "NT002"){
				scwin.select_sttn(false);
			}
			else if(info.newValue == "NT001"){
				scwin.select_cross(false);
			}
		}
		else { //정류소가 아니면 정류소 정보 초기화, 교차로가 아니면 교차로 정보 초기화
			dlt_BMS_ROUT_NODE_CMPSTN.setCellData(SI0402G1.getFocusedRowIndex(), "NODE_NM", "노드_" + util.getCurrentDate().substring(4));
			dlt_BMS_ROUT_NODE_CMPSTN.setCellData(SI0402G1.getFocusedRowIndex(), "CRS_ID", "");
			dlt_BMS_ROUT_NODE_CMPSTN.setCellData(SI0402G1.getFocusedRowIndex(), "STTN_ID", "");
			dlt_BMS_ROUT_NODE_CMPSTN.setCellData(SI0402G1.getFocusedRowIndex(), "STTN_NO", "");
		}
	}
}

scwin.dma_disp_check_onviewchange = function(info) {
	scwin.releaseGeoBtn();
	routMap.setDispCheck("map_SI0402", disp_check.getValue());
	routMap.drawRoute("map_SI0402", SI0402G1, SI0402G1.getFocusedRowIndex());
}

scwin.SI0402G1_onrowindexchange = function(row,oldRow) {
	routMap.focusNode("map_SI0402",SI0402G1,row);
};

scwin.sub_SI0402G1R0_submitdone = function(e) {


	scwin.refreshGridStatus();
	if(dlt_BMS_ROUT_NODE_CMPSTN.getTotalRow() == 0){
		routMap.initDisplay("map_SI0402");
	}
	else {
		//com.setFocusedCell(SI0402G1, "NODE_ID");
		if(SI0402G1.getFocusedRowIndex() == -1 ){
			SI0402G1.setFocusedCell( 0, "NODE_ID");
		}
		else {
			SI0402G1.setFocusedCell( SI0402G1.getFocusedRowIndex(), "NODE_ID");
		}
	}
	scwin.btn_sub_move();
	//routMap.drawRoute("map_SI0402", SI0402G1, -1, true);
};

scwin.sub_SI0402G1S0_submitdone = function(e) {
	var sCode = com.getResultCode(e);
	if (sCode == gcm.MESSAGE_CODE.STATUS_SAVE) {
		scwin.releaseGeoBtn();
		routMap.initMapInfo("map_SI0402");
		com.saveDone(SI0402G1,sub_SI0402G1R0);
	}
}

scwin.select_sttn = function(isAdd) {
	//oldNodeType = dlt_BMS_ROUT_NODE_CMPSTN.getCellData(SI0402G1.getFocusedRowIndex(),"NODE_TYPE");

	//var val_STTN_ID = dlt_BMS_ROUT_NODE_CMPSTN.getColData("STTN_ID");
	//dma_sub_search.set("STTN_IDS", val_STTN_ID);

	if (curRoutId != "") {
		var data = null;
		if(isAdd){
			data = { data : dma_sub_search.getJSON(), callbackFn : "scwin.add_sttn_done" };
		}
		else{
			data = { data : dma_sub_search.getJSON(), callbackFn : "scwin.select_sttn_done" };
		}
		var options = { id : "SI0402P03",
						popupName : "정류소선택", 
						modal : true};
		com.openPopup("/ui/BMS/SI0402/SI0402P03.xml", options, data);
	}
}

scwin.select_sttn_done = function(obj){
	if (typeof obj === "undefined") {
		dlt_BMS_ROUT_NODE_CMPSTN.setCellData(SI0402G1.getFocusedRowIndex(),"NODE_TYPE", oldNodeType);
	}
	else {
		dlt_BMS_ROUT_NODE_CMPSTN.setCellData(SI0402G1.getFocusedRowIndex(),"NODE_TYPE", routMap.NODE_TYPE.BUSSTOP);
		scwin.mappingNode("map_SI0402", SI0402G1, SI0402G1.getFocusedRowIndex(), obj, true);
	}
}

scwin.add_sttn_done = function(obj){
	if (typeof obj === "undefined") {

	}
	else {
		if(com.isEmpty(obj.GPS_Y)==false && com.isEmpty(obj.GPS_X)==false){
			curPoint.latLng.Ma = obj.GPS_Y;
			curPoint.latLng.La = obj.GPS_X;
		}
		var idx = routMap.addSttnByClick("map_SI0402", SI0402G1,curRoutId,curPoint);
		//dlt_BMS_ROUT_NODE_CMPSTN.setCellData(idx,"NODE_TYPE", routMap.NODE_TYPE.BUSSTOP);
		scwin.mappingNode("map_SI0402", SI0402G1, idx, obj, true);
	}
}

