scwin.mappingNode = function(mapId, grid, index, data, draggable){

	if(data==null)return;

	var nodeId = com.getGridViewDataList(grid).getCellData(index, "NODE_ID");
	//var routId = dlt_BMS_ROUT_MST.getCellData(SI0402G1.getFocusedRowIndex(),"ROUT_ID");
	if(com.getGridViewDataList(grid).getCellData(index,"NODE_TYPE")==routMap.NODE_TYPE.BUSSTOP){
		com.getGridViewDataList(grid).setCellData(index, "ROUT_ID",curRoutId);
		com.getGridViewDataList(grid).setCellData(index, "STTN_ID",data.STTN_ID);
		com.getGridViewDataList(grid).setCellData(index, "OLD_NODE_ID",nodeId);
		com.getGridViewDataList(grid).setCellData(index, "NODE_ID",data.STTN_ID);
		com.getGridViewDataList(grid).setCellData(index, "NODE_NM",data.STTN_NM);
		com.getGridViewDataList(grid).setCellData(index, "STTN_NO",data.STTN_NO);
	}
	else if(com.getGridViewDataList(grid).getCellData(index,"NODE_TYPE")==routMap.NODE_TYPE.CROSS){
		com.getGridViewDataList(grid).setCellData(index, "ROUT_ID",curRoutId);
		com.getGridViewDataList(grid).setCellData(index, "CRS_ID",data.CRS_ID);
		com.getGridViewDataList(grid).setCellData(index, "OLD_NODE_ID",nodeId);
		com.getGridViewDataList(grid).setCellData(index, "NODE_ID",data.CRS_ID);
		com.getGridViewDataList(grid).setCellData(index, "NODE_NM",data.CRS_NM);
	}
	routMap.drawRoute(mapId, grid, index, draggable);
}

